import React, { Component } from "react";

import * as actions from "./../../store/actions/actionTypes";
import { Route, Switch } from "react-router-dom";
import { connect } from "react-redux";
import classes from "./RespaldoBuilder.module.css";
import ContainerRespaldo from "../../components/Respaldo/Respaldo/ContainerRespaldo";
import Formas from "./../../components/Respaldo/OpcionesRespaldo/Formas/Formas";
import Medidas from "./../../components/Respaldo/OpcionesRespaldo/Medidas/Medidas";
import GenerosYColores from "./../../components/Respaldo/OpcionesRespaldo/GenerosYColores/GenerosYColores";
import Tachas from "./../../components/Respaldo/OpcionesRespaldo/Tachas/Tachas";
import Modelos from "./../../components/Respaldo/OpcionesRespaldo/Modelos/Modelos";
import Colores from "./../../components/Respaldo/OpcionesRespaldo/Colores/Colores";
import Mapa from "./../../components/Respaldo/Mapa/Mapa";
import ArrowForwardIosIcon from "@material-ui/icons/ArrowForwardIos";
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";

class RespaldoBuilder extends Component {
  state = {
    index: 0,
  };
  componentDidMount(){
    const baseUrl = "/respaldo-options/respaldo-builder";
    const keys = [
        "/",
        "/medidas",
        "/generosycolores",
        "/colores",
        "/tachas",
        "/modelos",
      ];
    let  location = this.props.history.location.pathname;

      location = location.replace(baseUrl, "");
    const ind = keys.indexOf(key => {
        console.log({key, location})
        return key == location});

    return console.log(ind);
}
  
  render() {

    
    const keys = [
      "/",
      "/medidas",
      "/generosycolores",
      "/colores",
      "/tachas",
      "/modelos",
    ];
    const baseUrl = "/respaldo-options/respaldo-builder";
    const toggleRoutesHandlerLeft = (location) => {
      location = location.replace(baseUrl, "");

      let index = this.state.index;

      if (keys[index - 1] === undefined) {
        return;
      }
      this.setState({ index: index - 1 });
      return this.props.history.push(baseUrl + keys[index - 1]);
    };

    const toggleRoutesHandlerRight = (location) => {
      location = location.replace(baseUrl, "");

      let index = keys.indexOf(location);

      if (keys[index + 1] === undefined) {
        return this.props.history.push("/resumen-respaldo");
      }
      if (index === -1) {
        index = 0;
      }
      this.setState({ index: index + 1 });
      return this.props.history.push(baseUrl + keys[index + 1]);
    };

    const routes = [
      "Formas",
      "Medidas",
      "Generos",
      "Colores",
      "Tachas",
      "Modelos",
    ];
    const pathname = this.props.history.location.pathname;

    //metodos para cambiar el respaldo...
    
    return (
      <div className={classes.RespaldoBuilder}>
        <ArrowForwardIosIcon
          className={classes.ArrowRight}
          onClick={() => toggleRoutesHandlerRight(pathname)}
        />

        <h2>
          #{this.state.index + 1}: {routes[this.state.index]}
        </h2>

        <ArrowBackIosIcon
          className={classes.ArrowLeft}
          onClick={() => toggleRoutesHandlerLeft(pathname)}
        />
        <Mapa routes={routes} index={this.state.index} />
        <ContainerRespaldo />
        <div className={classes.precioTotal}>
          Precio Total:<strong>100$</strong>
        </div>
        <div className={classes.control}>
          <Switch>
            <Route path={baseUrl + "/medidas"}>
              <Medidas
                medidas={this.props.respaldo.medidas}
                selectMedida={this.props.onMedidaSelected}
              />
            </Route>
            <Route path={baseUrl + "/generosycolores"}>
              <GenerosYColores 
              generos={this.props.respaldo.generosycolores} 
              selectGenero={this.props.onGeneroYColorSelected}/>
            </Route>
            <Route path={baseUrl + "/colores"}>
              <Colores 
              color={this.props.respaldo.color}
              generoParaColor={this.props.respaldo.generosycolores}
              selectColor={this.props.onColorSelected} />
            </Route>
            <Route path={baseUrl + "/tachas"}>
              <Tachas tachas={this.props.respaldo.tachas} />
            </Route>
            <Route path={baseUrl + "/modelos"}>
              <Modelos modelos={this.props.respaldo.modelos} />
            </Route>
            <Route path={baseUrl + "/"} exact>
              <Formas
                formas={this.props.respaldo.formas}
                selected={this.props.onFormaSelected}
              />
            </Route>
          </Switch>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    respaldo: state.respaldo.respaldo,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onFormaSelected: (tipo) =>
      dispatch({ type: actions.SET_FORMA, tipo: tipo }),
    onMedidaSelected: (altura, ancho) =>
      dispatch({ type: actions.SET_MEDIDAS, ancho: ancho, altura: altura }),
    onGeneroYColorSelected: ( tipo) => dispatch({type: actions.SET_GENEROYCOLOR, tipo:tipo }),
    onColorSelected: ( color) => dispatch({type: actions.SET_COLOR, color:color }),
    // onTachaSelected: (tamanio, tipo) => dispatch({type: actions.SET_FORMA, tamanio:tamanio, tipo: tipo}),
    // onModeloSelected: (tipo) => dispatch({type: actions.SET_FORMA, tipo: tipo}),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(RespaldoBuilder);
