import React, { Component } from 'react';

import classes from './Home.module.css';
import { Link } from 'react-router-dom';
import Logo from './../../components/Ul/Logo/Logo';

class Home extends Component {
    state={
        showMenu: false
    }

    showMenuHandler=()=>{
        this.setState({showMenu: !this.state.showMenu})
    }
   
    render() { 

        let classesMenu=[classes.menu]

        if(this.state.showMenu){
            classesMenu.push(classes.menuOpen)
        }

        return ( 
            <div className={classes.Home}>
                
                    <div className={classes.btn}>
                    <Link to="/nuestrosmodelos"><span >Quienes Somos</span></Link>
                      <span 
                      className={classes.respaldoLink}
                      onClick={this.showMenuHandler}
                      >Respaldos
                        <span  className={classesMenu.join(' ')}>
                        <Link to="/respaldo-options/nuestrosmodelos"><span >Modelos</span></Link>
                        <Link to="/respaldo-options/respaldo-builder"><span >Personalizalo</span></Link>
                        </span>
                    </span>
                      <Link to="/respaldo-options/respaldo-builder"><span >Contacto</span></Link>
                    </div>
                
                <div className={classes.container}>
                    <Logo />
                    <h3>Respaldos a tu medida</h3>
                </div>
            </div>
         );
    }
}
 
export default Home;
