import React from 'react'

import classes from './Colores.module.css';

function Colores(props) {
    let options = props.generos.map((genero, i) =>{
        if(genero.select){
            genero.color.map(color =>(
                <label >
                    <div className={classes.colorImg}></div>
                    <input type="checkbox" checked={props.color === color ? true: false} />
                </label>
            ))
        }
    })
    
    if(options === undefined){
        options = <p>Para visualizar los colores, primero debe elegir un genero. Vuelva al paso anterior.</p>
    }
    return (
        <div className={classes.Colores}>
            <h2>Elegi tu color</h2>
            <form>
            {options}
            </form>
        </div>
    )
}

export default Colores
