import React from "react";

import classes from "./Tachas.module.css";

const Tachas = (props) => {

  const arrayClasses=[classes.details, classes.fill];
  const arrayClasses2=[classes.tipo, classes.fill2];

  const opciones = props.tachas.map((tacha, i) => {
    
   return <details 
    className={tacha.select ? arrayClasses.join(' ') : arrayClasses[0]} 
    key={tacha.tamanio}
    onClick={() =>props.selectTacha(tacha.tamanio)}>
      <summary key={tacha.tamanio}>{tacha.tamanio} <span>${tacha.precio.toFixed(2)}</span></summary>
      <div className={classes.tiposTachas}>
        {
          tacha.tipo.map( tipo =>{
            
            return <div
            className={tacha.select ? arrayClasses2.join(' ') : arrayClasses2[0]} 
            key={Math.random()*100} 
            onClick={()=>props.selectTipoDeTacha(tipo.tipo)}>
              <img 
              alt="img_foto"
              src="https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcSD3X3H8ZEFWdCdWTqajSGyHUw8pDPiSxgcoA&usqp=CAU"/>
              <span>
              {tipo.tipo}
              </span>
              </div>
          })
        }

    </div>
    </details>
  });
  return (
    <div className={classes.Tachas}>
      <h2>Tachas</h2>
      <div>
        {opciones}  
      </div> 
    </div>
  );
};

export default Tachas;
