import * as actionTypes from './../actions/actionTypes';

const initialState={
    respaldo:{
        forma: 'no seleccionado',
        medida:{
            ancho: null,
            altura: null
        },
        genero:'no seleccionado',
        color: 'no seleccionado',
        tacha:'no seleccionado',
        tipoTacha:'',
        modelo:'no seleccionado',
        precioParticular: 0
    },
    displayTachaOptions: false,
 }

// const newPrecio = (addPrice) => ({
//         ...state.precioParticular, 
//          respaldoPrecio: state.precioParticular + addPrice
//         });



 const reducer = (state=initialState, action) =>{
    
    switch (action.type) {
      case actionTypes.SET_FORMA:
          let precio=null;
        if(state.forma === 'no seleccionado'){
            precio=action.precio
        }else{
            precio=state.precioParticular - precio;
            precio=state.precioParticular + action.precio;
        }
            return{
                ...state,
                respaldo: {
                    ...state.respaldo,
                    forma: action.tipo,
                    precioParticular: precio
                }
                
            }
      case actionTypes.SET_MEDIDAS:

                return{
                    ...state,
                    respaldo: {
                        ...state.respaldo,
                        medida:{
                            ancho: action.ancho,
                            altura: action.altura
                        },
                        precioParticular: state.respaldo.precioParticular+action.precio
                    }
                }
     case actionTypes.SET_GENERO:
    
                    return{
                        ...state,
                        respaldo:{
                            ...state.respaldo,
                            genero: action.tipo,
                            precioParticular: state.respaldo.precioParticular+action.precio
                        }
                    }
      case actionTypes.SET_COLOR:
          return{
              ...state,
              respaldo:{
                  ...state.respaldo,
                  color: action.color
              }
          }
      case actionTypes.SET_TACHA:
                        return{
                            ...state,
                            respaldo: {
                                ...state.respaldo,
                                tacha: action.tamanio,
                                tipoTacha: '',
                                precioParticular: state.respaldo.precioParticular+action.precio
                            }
                        }
    
      case actionTypes.SET_TIPO_TACHA:
          
                    return {
                             ...state,
                                respaldo: {
                                    ...state.respaldo,
                                    tipoTacha: action.tipo
                                }
                            }
     case actionTypes.SET_MODELO:
       
                            return{
                                ...state,
                                respaldo: {
                                    ...state.respaldo,
                                    modelo: action.tipo,
                                    precioParticular: state.respaldo.precioParticular+action.precio
                                }
                            }
     case actionTypes.DISPLAY_TACHA_OPTIONS:
         return{
             ...state,
            displayTachaOptions: !state.displayTachaOptions
         }
     case actionTypes.REFRESH_RESPALDO_PROPERTIES:
         return state=initialState
    
        default:
            return state;
    }
 }

 export default reducer;
