import * as actionTypes from './../actions/actionTypes';


const initialState ={
    basket: [],
    show: false,
    totalPrice: 0,
    loading: false,
    orders:[]
}

const reducer = (state= initialState, action) => {

    switch (action.type) {
        case actionTypes.ADD_TO_BASKET:
            
            return{
                ...state,
        basket: [...state.basket, action.producto],
            }
        case actionTypes.REMOVE_FROM_BASKET:
            const index= state.basket.findIndex( product =>product.precioParticular === action.precio);
            let newBasket = [...state.basket];
            newBasket.splice(index, 1);
            console.log(index, newBasket)
            return{
                ...state,
                basket: newBasket
            }
        case actionTypes.GET_TOTAL_PRICE:
            let totalPrice=0;
            if(action.basket.length > 0){
                let arrayPrice=[];
                totalPrice=action.basket.map(item => arrayPrice.push(item.precioParticular))
  
                totalPrice= arrayPrice.reduce((total, amount)=> total+amount)
            }
                return{
                    ...state,
                    totalPrice: totalPrice
                }
        case actionTypes.SHOW_INFO_BASKET:
            return{
                ...state,
                show: !state.show
            }
    case actionTypes.CREATE_ORDER:
                return{
                    ...state,
                    orders: [...state.orders, action.order]
                }
            case actionTypes.ON_LOADING:
            return{
                ...state,
                loading: !state.loading
            }
        default:
            return state;
    }
   
}

export default reducer;
