import React from "react";
import LinoSuizo from './ColoresSegunGenero/Lino/LinoSuizo';
import LinoCosta from './ColoresSegunGenero/Lino/LinoCosta';
import Ecocuero from './ColoresSegunGenero/Otros/Ecocuero';
import PanaDresden from './ColoresSegunGenero/Pana/PanaDresden';
import PanaPortugal from './ColoresSegunGenero/Pana/PanaPortugal';
import TussorLiso from './ColoresSegunGenero/Tussor/TussorLiso';
import TussorRayado from './ColoresSegunGenero/Tussor/TussorRayado';

import Tiza from './FotosDeGeneros/Tiza.png';
import Aluminio from './FotosDeGeneros/backRespaldo.png'

export const colores = (nombre) =>{
  let color={
      'Tiza': Tiza,
      'Aluminio': Aluminio,
      // 'Stone': Stone,
      // 'Ceniza': Ceniza,
      // 'Grafito': Grafito,
      // 'Carbón': Carbón,
      // 'Negro': Negro,
      // 'Natural': Natural,
      // 'Niebla': Niebla,
      // 'Nougat': Nougat,
      // 'Madera': Madera,
      // 'Lila': Lila,
      // 'Pink': Pink,
      // 'Limón': Limón,
      // 'Agua': Agua,
      // 'Turquesa': Turquesa,
      // 'Blue': Blue,
      // 'Azul Lavado': AzulLavado,
      // 'Denim': Denim,
      // 'Marino': Marino,
      // 'Pimenton': Pimenton,
      // 'Lacre': Lacre,
      // 'Guinda': Guinda, 
      // 'Ginger': Ginger, 
      // 'Café': Café, 
      // 'Arena': Arena
  }
  return color[nombre];
}



export const ColorDeFondo = (props) => {
  let color = null;
  switch (props.respaldo.genero) {
    case "Lino Suizo":
      color = <LinoSuizo color={props.respaldo.color} />;
      break;
    case "Lino Costa":
      color = <LinoCosta color={props.respaldo.color} />;
      break;
    case "Lino Paris":
      color = <image href={colores(props.color)} x="0" y="0" width="20" height="20" />;
      break;
    case "Pana Dresden":
      color = <PanaDresden color={props.respaldo.color} />;
      break;
    case "Pana Portugal":
      color = <PanaPortugal color={props.respaldo.color} />;
      break;
    case "Tussor Liso":
      color = <TussorLiso color={props.respaldo.color} />;
      break;
    case "Tussor Rayado":
      color = <TussorRayado color={props.respaldo.color} />;
      break;
    case "Ecocuero":
      color = <Ecocuero color={props.respaldo.color} />;
      break;
    default:
      color=null;
      break;
  }
  return (
    <defs>
      <pattern
        id="imgpattern"
        x="0" y="0" width="5" height="5"
        patternUnits="userSpaceOnUse"
      >
        {color}
      </pattern>
    </defs>
  );
};
