import React from "react";

import Tiza from './FotosDeGeneros/Tiza.png';
import Aluminio from './FotosDeGeneros/Aluminio.png';
import Ceniza from './FotosDeGeneros/Ceniza.png';

export const colores = (nombre) =>{
  let color={
    //Generos de Lino Paris
      'Tiza': Tiza,
      'Aluminio': Aluminio,
      // 'Stone': Stone,
      'Ceniza': Ceniza,
      // 'Grafito': Grafito,
      // 'Carbón': Carbón,
      // 'Negro': Negro,
      // 'Natural': Natural,
      // 'Niebla': Niebla,
      // 'Nougat': Nougat,
      // 'Madera': Madera,
      // 'Lila': Lila,
      // 'Pink': Pink,
      // 'Limón': Limón,
      // 'Agua': Agua,
      // 'Turquesa': Turquesa,
      // 'Blue': Blue,
      // 'Azul Lavado': AzulLavado,
      // 'Denim': Denim,
      // 'Marino': Marino,
      // 'Pimenton': Pimenton,
      // 'Lacre': Lacre,
      // 'Guinda': Guinda, 
      // 'Ginger': Ginger, 
      // 'Café': Café, 
      // 'Arena': Arena
  }
  return color[nombre];
}



export const ColorDeFondo = (props) => {
 
  let color = null;
  switch (props.respaldo.genero) {
    case "Lino Suizo":
      color = <image href={colores(props.respaldo.color)} x="0" y="0" width="20" height="20" />;
      break;
    case "Lino Costa":
      color = <image href={colores(props.respaldo.color)} x="0" y="0" width="20" height="20" />;
      break;
    case "Lino Paris":
      color = <image href={colores(props.respaldo.color)} x="0" y="0" width="20" height="20" />;
      break;
    case "Pana Dresden":
      color = <image href={colores(props.respaldo.color)} x="0" y="0" width="20" height="20" />;
      break;
    case "Pana Portugal":
      color = <image href={colores(props.respaldo.color)} x="0" y="0" width="20" height="20" />;
      break;
    case "Tussor Liso":
      color = <image href={colores(props.respaldo.color)} x="0" y="0" width="20" height="20" />;
      break;
    case "Tussor Rayado":
      color = <image href={colores(props.respaldo.color)} x="0" y="0" width="20" height="20" />;
      break;
    case "Ecocuero":
      color = <image href={colores(props.respaldo.color)} x="0" y="0" width="20" height="20" />;
      break;
    default:
      color=null;
      break;
  }
  return (
    <defs>
      <pattern
        id="imgpattern"
        x="0" y="0" width="5" height="5"
        patternUnits="userSpaceOnUse"
      >
        {color}
      </pattern>
    </defs>
  );
};
