import React from 'react';

import classes from './Formas.module.css'

const Formas = (props) => {
    const form=props.formas.map((forma,i) =>(
    <label><span className={classes.tipo}>{forma.tipo}</span>
    <span className={classes.precio}>${forma.precio}</span>
    <input type="checkbox" checked={forma.select} onClick={props.clicked(forma.tipo)}/></label>
    ));
    return ( 
        <div className={classes.Formas}>
            <h2>Elija la forma</h2>
            <form>
                {form}
            </form>
        </div>
     );
}
 
export default Formas;
