import React from 'react';

import classes from './Medidas.module.css'

const Medidas = (props) => {
    console.log(props)
    const form =props.medidas.map( (medida, i) =>
    <label key={Math.random() * 100}>
        <span>{medida.altura.toFixed(2)}</span><span> </span>
        <span >{medida.ancho.toFixed(2)}</span>
        <span className={classes.precioOpcion}>${medida.precio}</span>
        <input 
        type="checkbox" 
        checked={medida.select} 
        onChange={()=>props.selectMedida(medida.altura, medida.ancho)}/>
        </label>
    );
    //        return 

    return ( 
        <div className={classes.Medidas}>
            <div className={classes.title}>
                <span>Altura</span><span>x</span><span>Ancho</span>
                <span >Precio</span>
                <span>Select</span>
                </div>
            <form>
               {form}
            </form>
        </div>
     );
}
 
export default Medidas;
