import React, { Component } from "react";


import classes from "./Carrito.module.css";
import { connect } from "react-redux";
import * as actions from "./../../store/actions/actionTypes";
import ContactData from "./ContactData/ContactData";

class Carrito extends Component {
  render() {
    const productCards = this.props.basket.map((product, i) => {
      return (
        <div key={i} className={classes.productCard}>
          <h3>{product.modelo}</h3>
          <p>
            precio final: <strong>${product.precioParticular}</strong>
          </p>
          <span
            onClick={() =>
              this.props.onRemoveFromBasket(
                product.modelo,
                product.precioParticular
              )
            }
            className={classes.remover}
          >
            Remover
          </span>
        </div>
      );
    });

    return (
      <div className={classes.Carrito}>
        <h2>Tu Carrito</h2>
        <p>Productos</p>
        <div className={classes.container}>{productCards}</div>
        <p className={classes.envios}>
          Envios: los env√≠os son a convenir entre las partes. Las locaciones fuera de C.A.B.A o
          G.B.A tienen un recargo de $350 debido a los costos en el embalaje.
        </p>
        {this.props.basket.length === 0 ? (
          <p style={{ padding: "10px" }}>
            No tiene productos cargados en el carrito
          </p>
        ) : null}
        <ContactData />
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    basket: state.carrito.basket,
    price: state.carrito.totalPrice,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onRemoveFromBasket: (modelo, precio) =>
      dispatch({
        type: actions.REMOVE_FROM_BASKET,
        modelo: modelo,
        precio: precio,
      }),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Carrito);
