import * as actionTypes from './../actions/actionTypes';

const initialState ={
    basket:[]
}

const reducer = () =>{
    switch (action.type) {
        case actionTypes.ADD_TO_BASKET:
            return{
                ...state,
                basket: state.basket.push(action.item)
            }
            break;
    
        default:
           return state;
    }
}
