import React from "react";

import classes from "./ContainerRespaldo.module.css";
import Croquis from './Croquis/Croquis';

const Respaldo = (props) => {

 const arrayClasses=[classes.containerRespaldo];
 let tacha=null;
 let tipoDeTacha=null;
 let color=null
  let genero=null
  let ancho=null
  let altura=null
  if(props.respaldo.medida.ancho && props.respaldo.medida.altura){
    arrayClasses.push(classes.conMedidas);
    ancho=<span className={classes.ancho}>ancho: <strong>{props.respaldo.medida.ancho.toFixed(2)} m.</strong></span>
    altura=<span className={classes.altura}>altura: <strong>{props.respaldo.medida.altura.toFixed(2)} m.</strong></span>
  }
  if(props.respaldo.genero !== 'no seleccionado'){
    if(props.respaldo.color !== 'no seleccionado'){color=<p>color: {props.respaldo.color}</p>};
    if(props.respaldo.tacha !== 'no seleccionado' ){tacha=<p>tacha: {props.respaldo.tacha}</p>}
    console.log(props.respaldo.tipoTacha)
    if(props.respaldo.tipoTacha !== '' ){
      
      return tipoDeTacha=<p>tipo de tacha: {props.respaldo.tipoTacha}</p>}
  genero =<span className={classes.genero}><p>genero: {props.respaldo.genero}</p>{color}{tacha}{tipoDeTacha}</span>
  }
  return (
      <div className={arrayClasses.join(' ')}>
        {genero}
        {altura}
        <div className={classes.respaldo}>
          <Croquis respaldo={props.respaldo}/>
        </div>
        {ancho}
      </div>
  );
};

export default Respaldo;
