import React from "react";

import classes from "./Tachas.module.css";
import ArrowForwardIosIcon from "@material-ui/icons/ArrowForwardIos";


const Tachas = (props) => {
  
  const arrayClasses=[classes.itemHeader, classes.fill];
  const arrayClasses1=[classes.listaTipos, classes.open]
  const arrayClasses2=[classes.tipo, classes.fill2];

  const opciones = props.tachas.map((tacha, i) => (
    <ul  className={classes.lista} key={i}>
      <li className={classes.listItem} >
        <div 
        onClick={()=>props.selectTacha(tacha.tamanio, tacha.precio)}
        className={props.selectedTacha === tacha.tamanio ? arrayClasses.join(' '): arrayClasses[0]}>
          <span className={classes.arrow}><ArrowForwardIosIcon/></span>
          <span>{tacha.tamanio}</span>
          <span className={classes.price}>$ {tacha.precio}</span></div>
        <ul 
       className={props.displayOptions && props.selectedTacha === tacha.tamanio ? arrayClasses1.join(' '): arrayClasses1[0]}
        className={classes.innerList} >
          {tacha.tipo.map((modelo, i)=>{
            arrayClasses.push(classes.open-`${i}`);
            return(
            <li  
            onClick={()=>props.selectTipoDeTacha(modelo)}
            key={Math.random()*100}
            className={props.tipoDeTacha === modelo ? arrayClasses2.join(' '): arrayClasses2[0]}>
              <img
              className={classes.image}
              alt="img"
              src="https://d26lpennugtm8s.cloudfront.net/stores/718/076/products/73891531-96a5-4833-8d98-8b51cd9611c7_nube-385bafc285b6c688dd15290787244065-640-0.jpg"
              />
              <p>{modelo}</p></li>
          )})}
        </ul>
      </li>
    </ul>
  ))

  return (
    <div className={classes.Tachas}>
      <h2>Tachas</h2>
      <div className={classes.item}>
        {opciones}  
      </div> 
    </div>
  );
};


export default Tachas;
