import * as actionTypes from './../actions/actionTypes';


const initialState ={
    basket: [],
    show: false,
    totalPrice: 0
}

const reducer = (state= initialState, action) => {

    switch (action.type) {
        case actionTypes.ADD_TO_BASKET:
            return{
                ...state,
        basket: [...state.basket, action.producto],
            }
        case actionTypes.REMOVE_FROM_BASKET:
            const index= state.basket.findIndex( product =>{
                return (product.precioParticular === action.precio)
            });
            let newBasket = [...state.basket].splice(index, 1);
            console.log(index, newBasket)
            return{
                ...state,
                basket: newBasket
            }
        case actionTypes.SHOW_INFO_BASKET:
            return{
                ...state,
                show: true
            }
        case actionTypes.HIDE_INFO_BASKET:
                return{
                    ...state,
                    show: false
                }
        default:
            return state;
    }
   
}

export default reducer;
