import React from "react";

import "./Tachas.css";
import ArrowForwardIosIcon from "@material-ui/icons/ArrowForwardIos";


const Tachas = (props) => {
  
  const arrayClasses=["itemHeader", "fill"];
  const arrayClasses1=["listaTipos", "open"]
  const arrayClasses2=["tipo"];

  if(props.displayOptions){

  }

  const opciones = props.tachas.map((tacha, i) => (
    <ul  className="lista" key={i}>
      <li className="listItem" >
        <div 
        onClick={()=>props.selectTacha(tacha.tamanio, tacha.precio)}
        className={props.selectedTacha === tacha.tamanio ? arrayClasses.join(' '): arrayClasses[0]}>
          <span className="arrow"><ArrowForwardIosIcon/></span>
          <span>{tacha.tamanio}</span>
          <span className="price">$ {tacha.precio}</span></div>
        <ul 
       className={props.displayOptions && props.selectedTacha === tacha.tamanio ? arrayClasses1.join(' '): arrayClasses1[0]}
         >
          {tacha.tipo.map((modelo, i)=>{
            console.log(arrayClasses2)
            return(
            <li  
            onClick={()=>props.selectTipoDeTacha(modelo)}
            key={Math.random()*100}
            className={props.tipoDeTacha === modelo ? arrayClasses2.push(`open-${i}`).join(' '): arrayClasses2[0]}>
              <img
              className="image"
              alt="img"
              src="https://d26lpennugtm8s.cloudfront.net/stores/718/076/products/73891531-96a5-4833-8d98-8b51cd9611c7_nube-385bafc285b6c688dd15290787244065-640-0.jpg"
              />
              <p>{modelo}</p></li>
          )})}
        </ul>
      </li>
    </ul>
  ))

  return (
    <div className="Tachas">
      <h2>Tachas</h2>
      <div className="item">
        {opciones}  
      </div> 
    </div>
  );
};


export default Tachas;
