import React from 'react';

import classes from './NavigationItems.module.css';
import { Link } from 'react-router-dom';
import { Transition } from 'react-transition-group';

const NavigationItems = (props) => {
    console.log(props)
    let classesSubMenu=[classes.submenu]

    if(props.showSubMenu){
        classesSubMenu.push(classes.subMenuOpen)
    }
    return (
        <ul className={classes.NavigationItems}>
            <li className={classes.item} onClick={props.closed}>
                <Link to="/nuestrosmodelos"><span >Quienes Somos</span></Link>
            </li>
            <li 
            onClick={props.showSubmenuHandler}
            className={classes.item}>
                <span 
                      className={classes.respaldoLink}
                      >Respaldos</span>
                    <Transition 
                    in={props.showSubMenu} 
                    timeout={300}
                    mountOnEnter
                    unmountOnExit>
                        {state => (
                        <ul style={{
                            display: "flex",
                            justifyContent: "space-evenly",
                            color: "#404145",
                            position: "relative",
                            zIndex:"-1",
                            top: state==="entering" ? "45%":"-50%",
                            top: state==="entered" ? "45%":"-50%",
                            transition: "all 0.4s ease-out",
                            paddingInlineStart: "10px",
                            flexDirection: "column",
                        }}>
                        <Link to="/respaldo-options/nuestrosmodelos">
                         <span onClick={props.closed}>Modelos</span>
                        </Link>
                        <Link to="/respaldo-options/respaldo-builder">
                        <span onClick={props.closed}>Personalizalo</span>
                         </Link>
                      </ul>
                        )}
                     
                    </Transition>
            </li>
            <li 
            className={classes.item}
            onClick={props.closed}> 
            <Link to="/respaldo-options/respaldo-builder"><span >Contacto</span></Link></li>   
        </ul>
    )
}

export default NavigationItems;
