import * as actionTypes from './../actions/actionTypes';

const initialState={
    respaldo:{
        forma: '',
        medida:{
            ancho: null,
            altura: null
        },
        genero:'',
        color: '',
        tacha:'',
        tipoTacha:'',
        modelo:''
    },
    precioRespaldo: 0
 }

// const newPrecio = (addPrice) => ({
//         ...state.precioRespaldo, 
//          respaldoPrecio: state.precioRespaldo + addPrice
//         });



 const reducer = (state=initialState, action) =>{
    console.log(state.respaldo);
    switch (action.type) {
      case actionTypes.SET_FORMA:
 
            return{
                ...state,
                respaldo: {
                    ...state.respaldo,
                    forma: action.tipo
                },
                precioRespaldo: state.precioRespaldo + action.precio
            }
      case actionTypes.SET_MEDIDAS:

                return{
                    ...state,
                    respaldo: {
                        ...state.respaldo,
                        medida:{
                            ancho: action.ancho,
                            altura: action.altura
                        },
                    precioRespaldo: state.precioRespaldo+action.precio
                    }
                }
     case actionTypes.SET_GENEROYCOLOR:
    
                    return{
                        ...state,
                        respaldo:{
                            ...state.respaldo,
                            genero: action.tipo
                        },
                        precioRespaldo: state.precioRespaldo+action.precio
                    }
      case actionTypes.SET_COLOR:
          return{
              ...state,
              respaldo:{
                  ...state.respaldo,
                  color: action.color
              }
          }
      case actionTypes.SET_TACHA:
                        return{
                            ...state,
                            respaldo: {
                                ...state.respaldo,
                                tipoTacha: action.tamanio
                            },
                            precioRespaldo: state.precioRespaldo+action.precio
                        }
      case actionTypes.SET_TIPO_TACHA:
                            
                            return {
                                ...state,
                                respaldo: {
                                    ...state.respaldo,
                                    tipoTacha: action.tipo
                                }
                            }
     case actionTypes.SET_MODELO:
       
                            return{
                                ...state,
                                respaldo: {
                                    ...state.respaldo,
                                    modelo: action.tipo
                                },
                                precioRespaldo: state.precioRespaldo+action.precio
                            }
    
        default:
            return state;
    }
 }

 export default reducer;
