import React, { Component } from 'react';

import classes from './AdminLayout.module.css';
import { connect } from 'react-redux';
import axios from 'axios';
import Moment from 'react-moment';
import DataRespaldo from './../../components/Admin/DataRespaldo/DataRespaldo';
import DataCliente from './../../components/Admin/DataCliente/DataCliente';
import Spinner from './../../components/Ul/Spinner/Spinner';

class AdminLayout extends Component {

    state={
        orders:[],
        loading: false
    }

    componentDidMount(){
        this.setState({loading: true})
        axios.get('https://nookweb-5fb61.firebaseio.com/orders.json?auth=' + this.props.token)
        .then(response => {
            this.setState({loading: false})
            let data;
            let arrayOrders=[...this.state.orders]
            for(data in response.data){
                console.log()
                this.setState({orders: arrayOrders.push(response.data[data])});
            }
           

        })
        .catch(err => console.log(err))
    }

     
    render() { 
        console.log(this.state)
            let clasesPago=[classes.nopagado]
            let clasesEntregado=[classes.noentregado]

            let orders=this.state.orders.map(order=>{
            if(order.pagado){
                clasesPago.push(classes.pagado)
            }
            if(order.entregado){
                clasesPago.push(classes.entregado)
            }
            let pagado=<React.Fragment><span className={clasesPago.join(' ')}>PAGADO</span><span>cambiar a NO PAGADO</span></React.Fragment>
            if(!order.pagado){
                pagado=<React.Fragment><span className={clasesPago.join(' ')}>NO PAGADO</span><span>cambiar a PAGADO</span></React.Fragment>
            }
            let entregado=<label className={clasesEntregado.join(' ')}>ENTREGADO <input type="checkbox"/></label>

            return(
                <details className={classes.orden}>
                    <summary><span>{order.orderData.nombre}</span ><Moment className={classes.date}>{order.date}</Moment></summary>
                    <div className={classes.dataOrden}>
                    <div className={classes.pagosContainer}>{pagado}</div> 
                    {entregado}       
                    <DataRespaldo basket={order.basket}/>
                    <DataCliente cliente={order.orderData}/>
                    </div>
                </details>
            );
        })

        if(this.state.loading){
            return <Spinner />
        }

        return ( 
            <div className={classes.AdminLayout}>
                <h2 className={classes.title}>Admin</h2>
                <button className={classes.logoutBtn}>Logout</button>
                <div className={classes.orderContainer}>
                    {orders}
                </div>
            </div>
         );
    }
}
 
const mapStateToProps = state =>{
    return{
        token: state.auth.token
    }
}

export default connect(mapStateToProps, null)(AdminLayout);
