import React, { Component } from "react";

import axios from './../../axios';
import * as actions from "./../../store/actions/index";
import { Route, Switch } from "react-router-dom";
import { connect } from "react-redux";
import classes from "./RespaldoBuilder.module.css";
import ContainerRespaldo from "../../components/Respaldo/Respaldo/ContainerRespaldo";
import Formas from "./../../components/Respaldo/OpcionesRespaldo/Formas/Formas";
import Medidas from "./../../components/Respaldo/OpcionesRespaldo/Medidas/Medidas";
import GenerosYColores from "./../../components/Respaldo/OpcionesRespaldo/GenerosYColores/GenerosYColores";
import Tachas from "./../../components/Respaldo/OpcionesRespaldo/Tachas/Tachas";
import Modelos from "./../../components/Respaldo/OpcionesRespaldo/Modelos/Modelos";
import Colores from "./../../components/Respaldo/OpcionesRespaldo/Colores/Colores";
import Header from './../../components/Respaldo/Header/Header';
import Spinner from './../../components/Ul/Spinner/Spinner';


class RespaldoBuilder extends Component {
  state = {
    index: 0,
    opciones: null
  };
 
  componentDidMount() {

    axios.get('/opcionesRespaldo.json')
    .then(response => {
      const object=response.data;
      for(let property in object){
        this.setState({opciones: object[property]})
      }
    })
    .catch(error => console.log(error))




    const baseUrl = "/respaldo-options/respaldo-builder";
    const keys = [
      "",
      "/medidas",
      "/generosycolores",
      "/colores",
      "/tachas",
      "/modelos",
    ];
    let location = this.props.history.location.pathname;

    location = location.replace(baseUrl, "");

    let ind = keys.findIndex((key) => key === location);
    if (ind === -1) {
      return (ind = 0);
    }
    this.setState({ index: ind });
  }

  render() {
    const keys = [
      "/",
      "/medidas",
      "/generosycolores",
      "/colores",
      "/tachas",
      "/modelos",
    ];
    const baseUrl = "/respaldo-options/respaldo-builder";
    const toggleRoutesHandlerLeft = (location) => {
      location = location.replace(baseUrl, "");

      let index = this.state.index;

      if (keys[index - 1] === undefined) {
        return;
      }
      this.setState({ index: index - 1 });
      
      return this.props.history.push(baseUrl + keys[index - 1]);
    };

    const toggleRoutesHandlerRight = (location) => {
      location = location.replace(baseUrl, "");

      let index = keys.indexOf(location);

      if (keys[index + 1] === undefined) {
        return this.props.history.push("/resumen-respaldo");
      }
      if (index === -1) {
        index = 0;
      }
      //condicional: si el usuario no eligio forma no puede seguir:
      if(keys[index + 1] ===  "/medidas" && this.props.respaldo.forma === 'no seleccionado'){
        return alert('debe elegir una forma antes de proseguir')
      }else{
        this.setState({ index: index + 1 });
        return this.props.history.push(baseUrl + keys[index + 1]);
      }
    };

    const tachaSelect = ()=>{
      this.props.displayOptions()
      this.props.respaldo.tipoDeTacha()
    }

    if(this.state.opciones){
       return (
      <div className={classes.RespaldoBuilder}>
       <Header 
       index={this.state.index}
       toggleLeft={toggleRoutesHandlerLeft}
       toggleRight={toggleRoutesHandlerRight}/>
        <div className={classes.croquisRespaldo}>
          <ContainerRespaldo respaldo={this.props.respaldo}/>
          <div className={classes.precioTotal}>
            Precio Total:
            <strong>${this.props.precioRespaldo.toFixed(2)}</strong>
          </div>
        </div>
        <div className={classes.control}>
          <Switch>
           
            <Route path={baseUrl + "/medidas"}>
              <Medidas
              medidaSelected={this.props.respaldo.medida}
                medidas={this.state.opciones.medidas}
                selectMedida={this.props.onMedidaSelected}
              />
            </Route>
            <Route path={baseUrl + "/generosycolores"}>
              <GenerosYColores
                generos={this.state.opciones.generosycolores}
                generoSelected={this.props.respaldo.genero}
                selectGenero={this.props.onGeneroSelected}
              />
            </Route>
            <Route path={baseUrl + "/colores"}>
              <Colores
                color={this.state.opciones.generosycolores}
                generoParaColor={this.props.respaldo.genero}
                selectColor={this.props.onColorSelected}
                colorSelected={this.props.respaldo.color}
              />
            </Route>
            <Route path={baseUrl + "/tachas"}>
              <Tachas
                displayOptions={this.props.displayOptions}
                tachas={this.state.opciones.tachas}
                selectedTacha={this.props.respaldo.tacha}
                selectTacha={this.props.onTachaSelected}
                selectTipoDeTacha={this.props.onTipoDeTachaSelected}
                tipoDeTacha={this.tachaSelect}
                
              />
            </Route>
            <Route path={baseUrl + "/modelos"}>
              <Modelos
                selectModelo={this.props.onModeloSelected}
                selectedModelo={this.props.respaldo.modelo}
                modelos={this.state.opciones.modelos}
              />
            </Route>
            <Route path={baseUrl + "/"} exact>
              <Formas
                formas={this.state.opciones.formas}
                selected={this.props.onFormaSelected}
                tachaSelected={this.props.respaldo.forma}
              />
            </Route>
          </Switch>
        </div>
      </div>
    );
    }else{
      return <Spinner />
    }
    
   
  }
}

const mapStateToProps = (state) => {
  return {
    respaldo: state.respaldo.respaldo,
    displayOptions: state.respaldo.displayTachaOptions,
    precioRespaldo: state.respaldo.respaldo.precioParticular,
    basket: state.carrito.basket
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onFormaSelected: (tipo, precio) =>
      dispatch(actions.onFormaSelected(tipo, precio)),
    onMedidaSelected: (altura, ancho, precio) =>
      dispatch(actions.onMedidaSelected(altura, ancho, precio)),
    onGeneroSelected: (tipo, precio) =>
      dispatch(actions.onGeneroSelected(tipo, precio)),
    onColorSelected: (color) =>
      dispatch(actions.onColorSelected(color)),
    onTachaSelected: (tamanio, precio) =>
      dispatch(actions.onTachaSelected(tamanio, precio)),
    onTipoDeTachaSelected: (tipo) =>
      dispatch(actions.onTipoDeTachaSelected(tipo)),
    onModeloSelected: (tipo, precio) =>
      dispatch(actions.onModeloSelected(tipo, precio)),
    onClickOnTacha: ()=>dispatch(actions.onClickOnTacha())
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(RespaldoBuilder);
