import React from "react";

import classes from "./Tachas.module.css";

const Tachas = (props) => {
  console.log(props.tachas);
  const opciones = props.tachas.map((tacha, i) => {
    
   return <details 
    className={classes.details} 
    key={tacha.tamanio}
    onClick={() =>props.selectTacha(tacha.tamanio)}>
      <summary key={tacha.tamanio}>{tacha.tamanio}</summary>
      <div className={classes.tiposTachas}>
        {
          tacha.tipo.map( tipo =>{
            
            return <div
            className={classes.tipo}
            key={Math.random()*100} 
            onClick={()=>props.selectTipoDeTacha(tipo.tipo)}>
              <img 
              alt="img_foto"
              src="https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcSD3X3H8ZEFWdCdWTqajSGyHUw8pDPiSxgcoA&usqp=CAU"/>
              <span>
              {tipo.tipo}
              </span>
              </div>
          })
        }

        <span>precio: ${tacha.precio.toFixed(2)}</span>
    </div>
    </details>
  });
  return (
    <div className={classes.Tachas}>
      <h2>Tachas</h2>
      <div>
        {opciones}  
      </div> 
    </div>
  );
};

export default Tachas;
