import React, { Component } from 'react';
import Auxiliary from './../../hoc/Auxiliary';

import {TransitionMotion, spring, presets} from 'react-motion';
import classes from './Contacto.module.css'

class Contacto extends Component {
    state={
            animate: false
        }

    setAnimation(state){
        this.setState({animate: state});
    }
    render() { 
        
        return ( 
            <Auxiliary>
            <TransitionMotion>

            </TransitionMotion>
            <button onClick={() => this.setAnimation(true)}>Trigger Animation</button>
            <button onClick={() => this.setAnimation(false)}>Reset Animation</button>
            </Auxiliary>
         );
    }
}
 
export default Contacto;
