import React, { Component } from "react";

import withErrorHandler from "./../../../hoc/withErrorHandler/withErrorHandler";
import Input from "./../../../components/Ul/input/input";
import classes from "./ContactData.module.css";
import { connect } from "react-redux";
import Auxiliary from "./../../../hoc/Auxiliary";
import axios from "./../../../axios";
import { withRouter } from "react-router-dom";
import Spinner from "./../../../components/Ul/Spinner/Spinner";
import * as actions from "./../../../store/actions/index";

import moment from "moment";

class ContactData extends Component {
  state = {
    orderForm: {
      nombre: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "Ingresa tu nombre y apellido",
        },
        value: "",
        validation: {
          required: true,
        },
        valid: false,
        touched: false,
      },
      provincia: {
        elementType: "select",
        elementConfig: {
          option: [
            {
              value: "G.B.A (Gran Buenos Aires)",
              displayValue: "Gran Buenos Aires (G.B.A)",
            },
            {
              value: "Buenos Aires Interior",
              displayValue: "Buenos Aires Interior",
            },
            { value: "Catamarca", displayValue: "Catamarca" },
            { value: "C.A.B.A", displayValue: "C.A.B.A" },
            { value: "Chaco", displayValue: "Chaco" },
            { value: "Chubut", displayValue: "Chubut" },
            { value: "Cordoba", displayValue: "Cordoba" },
            { value: "Corrientes", displayValue: "Corrientes" },
            { value: "Entre Rios", displayValue: "Entre Rios" },
            { value: "Formosa", displayValue: "Formosa" },
            { value: "Jujuy", displayValue: "Jujuy" },
            { value: "La Pampa", displayValue: "La Pampa" },
            { value: "La Rioja", displayValue: "La Rioja" },
            { value: "Mendoza", displayValue: "Mendoza" },
            { value: "Misiones", displayValue: "Misiones" },
            { value: "Neuquen", displayValue: "Neuquen" },
            { value: "Rio Negro", displayValue: "Rio Negro" },
            { value: "Salta", displayValue: "Salta" },
            { value: "San Juan", displayValue: "San Juan" },
            { value: "San Luis", displayValue: "San Luis" },
            { value: "Santa Cruz", displayValue: "Santa Cruz" },
            { value: "Santa Fe", displayValue: "Santa Fe" },
            { value: "Sgo. del Estero", displayValue: "Sgo. del Estero" },
            { value: "Tierra del Fuego", displayValue: "Tierra del Fuego" },
            { value: "Tucuman", displayValue: "Tucuman" },
          ],
        },
        value: "G.B.A (Gran Buenos Aires)",
        validation: {
          required: true,
        },
        valid: true,
        touched: false,
      },
      email: {
        elementType: "input",
        elementConfig: {
          type: "email",
          placeholder: "Ingresá tu email",
        },
        value: "",
        validation: {
          required: true,
        },
        valid: false,
        touched: false,
      },
      telefono: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "Telefono",
        },
        value: "",
        validation: {
          required: true,
        },
        valid: false,
        touched: false,
      },
    },
    formaDePago: null,
    formIsValid: false,
    formaDeEnvio: null,
    loading: false,
  };

  orderHandler = (event) => {
    this.setState({ loading: true });
    event.preventDefault();
    const formData = {};
    for (let formElementIdentifier in this.state.orderForm) {
      formData[formElementIdentifier] = this.state.orderForm[
        formElementIdentifier
      ].value;
    }

    let time = moment().format("MMMM Do YYYY, h:mm:ss a");
    const order = {
      basket: this.props.basket,
      price: this.props.price,
      orderData: formData,
      formaDePago: this.props.formaDePago,
      pagado: false,
      entregado: false,
      date: time,
    };

    axios
      .post("/orders.json", order)
      .then((response) => {
        this.setState({ loading: false });
        // this.props.history.push('/')
      })
      .catch((error) => console.log(error)); //this.props.onLoading());
  };

  checkValidity(value, rules) {
    let isValid = true;

    if (rules.required) {
      isValid = value.trim() !== "" && isValid;
    }

    if (rules.minLength) {
      isValid = value.length >= rules.minLength && isValid;
    }

    return isValid;
  }

  inputChangeHandler = (event, inputIdentifier) => {
    const updatedOrderForm = {
      ...this.state.orderForm,
    };
    const updatedFormElement = {
      ...updatedOrderForm[inputIdentifier],
    };

    updatedFormElement.value = event.target.value;
    updatedFormElement.valid = this.checkValidity(
      updatedFormElement.value,
      updatedFormElement.validation
    );
    updatedFormElement.touched = true;
    updatedOrderForm[inputIdentifier] = updatedFormElement;

    let formIsValid = true;
    for (let inputIdentifier in updatedOrderForm) {
      formIsValid = updatedOrderForm[inputIdentifier].valid && formIsValid;
    }
    this.setState({ orderForm: updatedOrderForm, formIsValid: formIsValid });
  };

  pagoHandler = (event) => {
    let formaDePago = event.target.value;
    this.props.onFormaDePagoSelected(formaDePago);
  };

  envioHandler =(event) =>{
    let formaDeEnvio = event.target.value;
    this.props.onSelectFormaDeEnvio(formaDeEnvio)
  }

  render() {
    console.log(this.props.formaDePago);
    const arrayClasses = [classes.submitBtn, classes.disabled];
    const formElementsArray = [];
    for (let key in this.state.orderForm) {
      formElementsArray.push({
        id: key,
        config: this.state.orderForm[key],
      });
    }

    let classesPago1 = [classes.opPago1];
    let classesPago2 = [classes.opPago2];
    let classesSelectorPago1=[classes.selectorPago]
    let classesSelectorPago2=[classes.selectorPago]

    if(this.props.formaDePago === 'MercadoPago'){
        classesPago2.push(classes.selectFormaDePago)
        classesSelectorPago2.push(classes.activePago)
    }
    if(this.props.formaDePago === 'Transferencia Bancaria'){
        classesPago1.push(classes.selectFormaDePago)
        classesSelectorPago1.push(classes.activePago)
    }

    let form = (
      <form className={classes.ContactData} onSubmit={this.orderHandler}>
        <h4 className={classes.titleOpcionesDeEnvio}>Opciones de Envio</h4>
        <div className={classes.formaDeEnvio}>
          <div className={classes.op1}>
            <label className={classes.tipoenvioCard1}>
              <input type="radio" name="envio" id="Retiro" value="Retiro" onChange={this.envioHandler} />
              <div className={classes.selector}></div>
              <div className={classes.info1}>
                <span>
                  <h4 className={classes.precioRetiro}>GRATIS</h4>
                  <div>Retiras por San Isidro</div>
                  <p>
                    Holmberg 875, Boulogne, San Isidro. Atencion de lunes a
                    viernes de 10 a 18hs.
                  </p>
                </span>
              </div>
            </label>
          </div>
          <div className={classes.op2}>
            <label className={classes.tipoenvioCard2}>
              <input
                type="radio"
                name="envio"
                id="A convenir"
                value="A convenir"
                onChange={this.envioHandler}
              />
              <div className={classes.selector}></div>
              <div className={classes.info2}>
                <span>
                  <div>A convenir</div>
                  <p>
                    Los costos del envío quedan a cargo del comprador. Las
                    locaciones fuera de C.A.B.A o G.B.A tienen un recargo de
                    $350 debido a los costos del embalaje. Telefono de
                    contacto:+54 911 55607284
                  </p>
                </span>
              </div>
            </label>
          </div>
        </div>

        <h4 className={classes.datosContacto}>Datos de contacto</h4>
        {formElementsArray.map((formElement) => {
          return (
            <Input
              key={formElement.id}
              elementType={formElement.config.elementType}
              elementConfig={formElement.config.elementConfig}
              value={formElement.config.value}
              invalid={!formElement.config.valid}
              shouldValidate={formElement.config.validation}
              touched={formElement.config.touched}
              changed={(event) =>
                this.inputChangeHandler(event, formElement.id)
              }
            />
          );
        })}
        <h4 className={classes.datosPago}>Opciones de Pago</h4>
        <div className={classes.formaDePago}>
          <div className={classesPago1.join(' ')}>
            <label className={classes.tipoPagoCard1}>
              <input
                type="radio"
                name="formaDePago"
                id="Transferencia Bancaria"
                value="Transferencia Bancaria"
                onChange={this.pagoHandler}
              />
              <div className={classesSelectorPago1.join(' ')}></div>
              <div className={classes.infoPago1}>
                <span>
                  <div>Transferencia Bancaria</div>
                  <p>
                    Te enviaremos un email con nuestros datos bancarios para que
                    nos transfieras
                  </p>
                </span>
              </div>
            </label>
          </div>
          <div className={classesPago2.join(' ')}>
            <label className={classes.tipoPagoCard2}>
              <input
                type="radio"
                name="formaDePago"
                id="MercadoPago"
                value="MercadoPago"
                onChange={this.pagoHandler}
              />
              <div className={classesSelectorPago2.join(' ')}></div>
              <div className={classes.infoPago2}>
                <span>
                  <div>MercadoPago</div>
                  <p>
                    Paga con tarjetas de débito, crédito o con tu dinero
                    guardado en MercadoPago. También ofrecemos pagos en cuotas.
                  </p>
                </span>
              </div>
            </label>
          </div>
        </div>
        <button
          type="submit"
          className={
            this.props.formaDePago !== null &&
            this.props.basket.length !== 0 &&
            this.state.formIsValid
              ? arrayClasses[0]
              : arrayClasses.join(" ")
          }
          disabled={
            this.props.formaDePago === null &&
            !this.state.formIsValid &&
            this.props.basket.length === 0
          }
        >
          Proceder al pago
        </button>
      </form>
    );
    if (this.state.loading) {
      form = <Spinner />;
    }

    return <Auxiliary>{form}</Auxiliary>;
  }
}

const mapStateToProps = (state) => {
  return {
    basket: state.carrito.basket,
    price: state.carrito.totalPrice,
    formaDePago: state.carrito.formaDePago,
    formaDeEnvio: state.carrito.formaDeEnvio
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onCreateOrder: (order) => dispatch(actions.onCreateOrder(order)),
    onFormaDePagoSelected: (formaDePago) =>
      dispatch(actions.onSelectFormaDePago(formaDePago)),
    onSelectFormaDeEnvio: (formaDeEnvio)=>(actions.onSelectFormaDeEnvio(formaDeEnvio))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withRouter(withErrorHandler(ContactData, axios)));

