      classesPrecio.push(classes.precioConForma)
    }

    const pathname = this.props.history.location.pathname;
    
    let arrowRight = (
      <svg
      className={classes.ArrowRight}
      onClick={() => toggleRoutesHandlerRight(pathname)}
        width="37"
        height="50"
        viewBox="0 0 37 74"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
      >
        <path d="M3 2L33 38.8L5.17391 72" stroke="#404145" strokeWidth="6" />
      </svg>
    );
  
    let arrowLeft = (
      <svg
      className={classes.ArrowLeft}
      onClick={() => toggleRoutesHandlerLeft(pathname)}
        width="37"
        height="50"
        viewBox="0 0 37 74"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
      >
        <path d="M34 72L4 35.2L31.8261 2" stroke="#404145" strokeWidth="6" />
      </svg>
    );

  return(
      <div className={classes.RespaldoBuilder}>
        
       <Header 
       index={this.state.index}
       toggleLeft={toggleRoutesHandlerLeft}
       toggleRight={toggleRoutesHandlerRight}/>
       
        <div className={this.props.respaldo.forma === 'no seleccionado' ? null: classes.croquisRespaldo}>
        
        <CurrencyFormat 
                renderText={(value)=>(
                <div className={classesPrecio.join(' ')}>
                        Precio Total:
            <strong>{value}</strong>
          </div>
                        )
                }
                decimalScale={2}
                value={this.props.precioRespaldo} 
                thousandSeparator={true}
                prefix={"$"}
                displayType={"text"}
            />

            
          <ContainerRespaldo respaldo={this.props.respaldo}/>
        </div>

        <div className={classes.control}>
          {arrowLeft}
          {arrowRight}
          
          <TransitionGroup>
          <CSSTransition
          timeout={300}
          className="fade">
          <Switch>
            <Route path={baseUrl + "/medidas"}>
              <Medidas
              medidaSelected={this.props.respaldo.medida}
                medidas={this.state.opciones.medidas}
                selectMedida={this.props.onMedidaSelected}
              />
            </Route>
            <Route path={baseUrl + "/generosycolores"}>
              <GenerosYColores
                generos={this.state.opciones.generos}
                generoSelected={this.props.respaldo.genero}
                selectGenero={this.props.onGeneroSelected}
              />
            </Route>
            <Route path={baseUrl + "/colores"}>
              <Colores
                color={this.state.opciones.generos}
                generoParaColor={this.props.respaldo.genero}
                selectColor={this.props.onColorSelected}
                colorSelected={this.props.respaldo.color}
              />
            </Route>
            <Route path={baseUrl + "/tachas"}>
              <Tachas
                displayOptions={this.props.displayOptions}
                tachas={this.state.opciones.tachas}
                selectedTacha={this.props.respaldo.tacha}
                selectTacha={tachaSelect}
                selectTipoDeTacha={tipoDeTachaSelect}
                tipoDeTacha={this.props.respaldo.tipoTacha}
                
              />
            </Route>
            <Route path={baseUrl + "/modelos"}>
              <Modelos
                selectModelo={this.props.onModeloSelected}
                selectedModelo={this.props.respaldo.modelo}
                modelos={this.state.opciones.modelos}
              />
            </Route>
            <Route path={baseUrl + "/"} exact>
              <Formas
                formas={this.state.opciones.formas}
                selected={this.props.onFormaSelected}
                tachaSelected={this.props.respaldo.forma}
              />
            </Route>
          </Switch>
          </CSSTransition>
          </TransitionGroup>
        </div>
      </div>
    );
              }
              
}

const mapStateToProps = (state) => {
  return {
    respaldo: state.respaldo.respaldo,
    displayOptions: state.respaldo.displayTachaOptions,
    precioRespaldo: state.respaldo.respaldo.precioParticular,
    basket: state.carrito.basket
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onFormaSelected: (tipo, precio) =>
      dispatch(actions.onFormaSelected(tipo, precio)),
    onMedidaSelected: (altura, ancho, precio) =>
      dispatch(actions.onMedidaSelected(altura, ancho, precio)),
    onGeneroSelected: (tipo, precio) =>
      dispatch(actions.onGeneroSelected(tipo, precio)),
    onColorSelected: (color) =>
      dispatch(actions.onColorSelected(color)),
    onTachaSelected: (tamanio, precio) =>
      dispatch(actions.onTachaSelected(tamanio, precio)),
    onTipoDeTachaSelected: (tipo) =>
      dispatch(actions.onTipoDeTachaSelected(tipo)),
    onModeloSelected: (tipo, precio) =>
      dispatch(actions.onModeloSelected(tipo, precio)),
    onClickOnTacha: ()=>dispatch(actions.onClickOnTacha())
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(RespaldoBuilder);
