import React from 'react';

import classes from './ResumenRespaldo.module.css';
import { connect } from 'react-redux';
import Modal from './../../Ul/Modal/Modal';
import Message from './../../Message/message';

function ResumenRespaldo(props) {
    return (
        <div className={classes.ResumenRespaldo}>
            <h2>Resumen Respaldo</h2>
            <ul className={classes.listaPrincipal}>
                <li>Envio: tu modelo tarda 10 dias habiles</li>
                <li >Caracteristicas:
                    <ul className={classes.Caracteristicas}>
                        <li>Forma: {props.respaldo.forma}</li>
                        <li>Medidas: 
                            <ul className={classes.Medidas}>
                                <li>altura: {props.respaldo?.medida.altura ?props.respaldo?.medida.altura.toFixed(2): '0'} m.</li>
                                <li>ancho: {props.respaldo?.medida.ancho ?props.respaldo?.medida.ancho.toFixed(2): '0'} m.</li>
                            </ul>
                            </li>
                         <li>Genero: {props.respaldo.genero}</li>  
                         <li>Color: {props.respaldo.color}</li>  
                        <li>Tachas: {props.respaldo.tachas} {props.respaldo.tipoTacha}</li> 
                        <li>Modelo: {props.respaldo.modelo}</li> 
                    </ul>
                </li>
                <li>Precio final:</li>
            </ul>
            <button className={classes.Button}>AGREGAR AL CARRITO</button>
            <Modal >
                <Message />
            </Modal>
        </div>
    )
}

const mapStateToProps = state =>{
    return{
            respaldo: state.respaldo.respaldo
    }
}

export default connect(mapStateToProps,null)(ResumenRespaldo);
