import React from "react";

import classes from "./Colores.module.css";

function Colores(props) {
  let generoSelected = props.generoParaColor.findIndex((genero) => {
    return genero.select = true;
  });

  let options = null;
  if (generoSelected === -1) {
    return (options = (
      <p>
        Para seleccionar un color, primero seleccione un genero. Vuelva al paso
        anterior.
      </p>
    ));
  } else {
    props.generoParaColor[generoSelected].color.map(
      color =>
        (options = (
          <label>
            <div className={classes.colorImg}></div>
            <input
              type="checkbox"
              checked={props.color === color ? true : false}
            />
          </label>
        ))
    );
  }

  return (
    <div className={classes.Colores}>
      <h2>Elegi tu color</h2>
      <form>{options}</form>
    </div>
  );
}

export default Colores;
