import React, { Component } from 'react';

import * as actions from "./../../../store/actions/actionTypes";
import { withRouter, Link } from "react-router-dom";
import classes from "./ResumenRespaldo.module.css";
import { connect } from "react-redux";
import Modal from "./../../Ul/Modal/Modal";
import Message from "./../../Message/message";
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";



class ResumenRespaldo extends Component {

    render(){

        const arrayClasses = [classes.Button, classes.Disabled];
  
        const checkFeatures = Object.values(props.caracteristicas).some(
          (item) =>
            item === "no seleccionado" || item.ancho === null || item.altura === null
        );
          const show=false
          const addToBasket = () =>{
              props.onProductAddedToBasket(props.caracteristicas)
          }

          return (
    <div className={classes.ResumenRespaldo}>
      <h2>Resumen Respaldo</h2>
      <Link to="/respaldo-options/respaldo-builder/modelos">
        {" "}
        <ArrowBackIosIcon /> Volver
      </Link>
      <ul className={classes.listaPrincipal}>
        <li>Envio: tu modelo tarda 10 dias habiles</li>
        <li>
          Caracteristicas:
          <ul className={classes.Caracteristicas}>
            <li>Forma: {props.caracteristicas.forma}</li>
            <li>
              Medidas:
              <ul className={classes.Medidas}>
                <li>
                  altura:{" "}
                  {props.caracteristicas?.medida.altura
                    ? props.caracteristicas?.medida.altura.toFixed(2)
                    : "0"}{" "}
                  m.
                </li>
                <li>
                  ancho:{" "}
                  {props.caracteristicas?.medida.ancho
                    ? props.caracteristicas?.medida.ancho.toFixed(2)
                    : "0"}{" "}
                  m.
                </li>
              </ul>
            </li>
            <li>Genero: {props.caracteristicas.genero}</li>
            <li>Color: {props.caracteristicas.color}</li>
            <li>
              Tachas: {props.caracteristicas.tachas}{" "}
              {props.caracteristicas.tipoTacha}
            </li>
            <li>Modelo: {props.caracteristicas.modelo}</li>
          </ul>
        </li>
        <li>Precio final:</li>
      </ul>
      {checkFeatures ? (
        <span>
          <p>
            Para agregar al carrito su respaldo debe contar con todas las
            caracteristicas seleccionadas
          </p>
          <Link to="/respaldo-options/respaldo-builder/modelos">Volver</Link>
        </span>
      ) : null}
      <button
        // disabled={checkFeatures}
        onClick={addToBasket}
        className={checkFeatures ? arrayClasses.join(" ") : arrayClasses[0]}
      >
        AGREGAR AL CARRITO
      </button>
      <Modal>
        <Message />
      </Modal>
    </div>
  );
    }

}

const mapStateToProps = (state) => {
  return {
    respaldo: state.respaldo,
    caracteristicas: state.respaldo.respaldo,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onProductAddedToBasket: (producto) =>
      dispatch({ type: actions.ADD_TO_BASKET, producto: producto }),
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withRouter(ResumenRespaldo));
