import React, { Component } from 'react';

import { Link } from 'react-router-dom';
import classes from './Carrito.module.css';
import { connect } from 'react-redux';

class Carrito extends Component {

    render() { 

        const productCards=this.props.basket.map( product =>{
            return (<div className={classes.productCard}>
                    <h3>{product.modelo}</h3>
            <p>precio final: <strong>{product.precioParticular}</strong></p>
            <p>precio: {product.precioParticular}</p>
                <span className={classes.remover}>Remover</span>
                    </div>)
        })

        return ( 
            <div className={classes.Carrito}>
                <h2>Tu Carrito</h2>
                <p>Productos</p>
                <div className={classes.container}>
                {productCards}
                </div>
                <p className={classes.envios}>Envios:</p>
                {this.props.basket.length === 0 ? <p style={{padding: "10px"}}>
                    No tiene productos cargados al carrito</p>: null}
                <button
                className={this.props.basket.length === 0 ? classes.disabled : null}
                disabled={this.props.basket.length === 0}
                >Proceder al pago</button>
            </div>
         );
    }
}

const mapStateToProps = state =>{
    return{
        basket: state.carrito.basket
    }
}

 
export default connect(mapStateToProps, null)(Carrito);
