import React from "react";

import classes from "./Formas.module.css";

const Formas = (props) => {
  
    const arrayClasses=[classes.item, classes.fill];


  const options = props.formas.map((forma, i) =>{ 
    let croquis='';
    if(forma.tipo === "Rectangular"){
      croquis=(
        <svg width="110" height="62" viewBox="0 0 110 62" fill="none" xmlns="http://www.w3.org/2000/svg">
        <rect x="0.25" y="0.25" width="109.5" height="61.5" stroke="#404145" strokeWidth="0.5"/>
      </svg>
      )
    }
      if(forma.tipo === "Capilla"){
    croquis=(
      <svg width="112" height="64" viewBox="0 0 112 64" fill="none" xmlns="http://www.w3.org/2000/svg">
      <path d="M1 63V12.8182C11.829 12.8182 36.9067 4.63636 36.9067 4.63636C36.9067 4.63636 54.5751 -3.54545 76.2332 4.63638C97.8912 12.8182 111 12.8182 111 12.8182V63H1Z" stroke="#404145" strokeWidth="0.5"/>
      </svg>
  
)
      }
      if(forma.tipo === "Oval"){
        croquis=(   
      <svg width="110" height="62" viewBox="0 0 110 62" fill="none" xmlns="http://www.w3.org/2000/svg">
      <path d="M0.25 62.0569V13.0178C32.1357 -3.60213 78.8729 -4.40309 109.75 13.0122V62.0569H0.25Z" fill="white" stroke="#404145" strokeWidth="0.5"/>
      </svg>
     )
      }
        if(forma.tipo === "Esquinas Redondas"){
          croquis=(
            <svg width="110" height="62" viewBox="0 0 110 62" fill="none" xmlns="http://www.w3.org/2000/svg">
<g clip-path="url(#clip0)">
<path opacity="0.3" d="M0.25 0.25H109.75V61.75H0.25V0.25Z" stroke="#404145" strokeWidth="0.5"/>
</g>
<path d="M110 62V62.5H110.5V62H110ZM0 62H-0.5V62.5H0V62ZM20 0.5H90V-0.5H20V0.5ZM109.5 20V62H110.5V20H109.5ZM110 61.5H0V62.5H110V61.5ZM0.5 62V20H-0.5V62H0.5ZM90 0.5C100.77 0.5 109.5 9.23045 109.5 20H110.5C110.5 8.67816 101.322 -0.5 90 -0.5V0.5ZM20 -0.5C8.67816 -0.5 -0.5 8.67816 -0.5 20H0.5C0.5 9.23044 9.23045 0.5 20 0.5V-0.5Z" fill="#404145"/>
<defs>
<clipPath id="clip0">
<path d="M0 20C0 8.9543 8.95431 0 20 0H90C101.046 0 110 8.9543 110 20V62H0V20Z" fill="white"/>
</clipPath>
</defs>
</svg>

            
          )
              }
  
  
  return (
  <div key={forma.tipo} className={forma.tipo === props.tachaSelected ? arrayClasses.join(' '):arrayClasses[0]} 
    onClick={() => props.selected(forma.tipo, forma.precio)}>
      <span className={classes.croquis}>
      {croquis}
      </span>
      <span className={classes.tipo}>{forma.tipo}</span>
      <span className={classes.precio}>${forma.precio}</span>
    </div>
    )
    });
  
  return (
    <div className={classes.Formas}>
      <h2>Eleg√≠ la forma</h2>
      <div className={classes.guideline}>
        <span className={classes.formaGuideline}>Forma</span>
        <span>Precio</span>
      </div>
      <div className={classes.options}>{options}</div>
    </div>
  );
};

export default Formas;
