import React from 'react';
import Auxiliary from '../../../hoc/Auxiliary';
import ShoppingBasketOutlinedIcon from '@material-ui/icons/ShoppingBasketOutlined';

import * as actions from './../../../store/actions/actionTypes';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import classes from './CarritoLink.module.css';
import CarritoCards from './CarritoCards/CarritoCards';

function Carrito(props) {

    // let infoCarrito;
    // if(props.basket.length >0 ){
    //   return infoCarrito= props.basket.map(item => {
    //       return  Object.keys(item).map(itemKey =>{
    //           return [...Array(item[itemKey])].map(key =>{
    //             console.log(itemKey, key)
    //            if(key.toString() === '[object Object]'){
    //                console.log(key)
    //                return Object.values(key).map( value => <span>{value.toFixed(2)}m.</span> )
    //            }
    //            if(key === ''){return null};
    //           return <span><p>{itemKey}:{key}</p></span>
    //           })
    //           })
    //    })
    // }else{
    //     infoCarrito=<p style={{fontSize: '0.8rem'}}>No tiene productos cargados al carrito.</p>
    // }
   const arrayClasses =[classes.info, classes.show];


    return (
        <div>
            <Link 
            onMouseEnter={() =>props.showInfo()}
            onMouseLeave={() =>props.hideInfo()} 
            to="/carrito" 
            className={classes.Carrito}><ShoppingBasketOutlinedIcon /></Link>
            <div 
             onMouseEnter={() =>props.showInfo()}
             onMouseLeave={() =>props.hideInfo()} 
            className={props.show ? arrayClasses.join(' '): arrayClasses[0]} >
                <div>
                <CarritoCards basket={this.props.basket}/>
                </div>
            </div>
        </div>
    )
}

const mapStateToProps = state =>{
    return{
        basket: state.carrito.basket,
        show: state.carrito.show
    }
}

const mapDispatchToProps = dispatch =>{
    return{
        showInfo: ()=> dispatch({type: actions.SHOW_INFO_BASKET}),
        hideInfo: ()=> dispatch({type: actions.HIDE_INFO_BASKET})
    }
}


export default connect(mapStateToProps, mapDispatchToProps)(Carrito)
