import React, { Component } from 'react';

import withErrorHandler from './../../../hoc/withErrorHandler/withErrorHandler';
import Input from './../../../components/Ul/input/input';
import classes from './ContactData.module.css'
import { connect } from 'react-redux';
import Auxiliary from './../../../hoc/Auxiliary';
import axios from './../../../axios';
import { withRouter } from 'react-router-dom';
import Spinner from './../../../components/Ul/Spinner/Spinner';
import * as actions from './../../../store/actions/index';

import moment from 'moment';

class ContactData extends Component {

    

    state={
        orderForm:{
                nombre:{
                    elementType: 'input',
                    elementConfig:{
                        type: 'text',
                        placeholder: 'Ingresa tu nombre y apellido'
                    },
                    value: '',
                    validation:{
                        required: true
                    },
                    valid: false,
                    touched: false
                },
                provincia:{
                    elementType: 'select',
                    elementConfig:{
                option:[
                        {value:"G.B.A (Gran Buenos Aires)", displayValue:"Gran Buenos Aires (G.B.A)"},
                        {value:"Buenos Aires Interior", displayValue:"Buenos Aires Interior"},
                        {value:"Catamarca", displayValue:"Catamarca"},
                        {value:"C.A.B.A", displayValue:"C.A.B.A"},  
                        {value:"Chaco", displayValue:"Chaco"},                       
                        {value:"Chubut", displayValue:"Chubut"},                        
                        {value:"Cordoba", displayValue:"Cordoba"},
                        {value:"Corrientes", displayValue:"Corrientes"},
                        {value:"Entre Rios", displayValue:"Entre Rios"},
                        {value:"Formosa", displayValue:"Formosa"},
                        {value:"Jujuy", displayValue:"Jujuy"},                       
                        {value:"La Pampa", displayValue:"La Pampa"},
                        {value:"La Rioja", displayValue:"La Rioja"},
                        {value:"Mendoza", displayValue:"Mendoza"},
                        {value:"Misiones", displayValue:"Misiones"},
                        {value:"Neuquen", displayValue:"Neuquen"},
                        {value:"Rio Negro", displayValue:"Rio Negro"},
                        {value:"Salta", displayValue:"Salta"},                       
                        {value:"San Juan", displayValue:"San Juan"},
                        {value:"San Luis", displayValue:"San Luis"},
                        {value:"Santa Cruz", displayValue:"Santa Cruz"},
                        {value:"Santa Fe", displayValue:"Santa Fe"},
                        {value:"Sgo. del Estero", displayValue:"Sgo. del Estero"},
                        {value:"Tierra del Fuego", displayValue:"Tierra del Fuego"},
                        {value:"Tucuman", displayValue:"Tucuman"},
                        ]
                    },
                    value: 'G.B.A (Gran Buenos Aires)',
                    validation:{
                        required: true
                    },
                    valid: true,
                    touched: false
                },
                email:{
                    elementType: 'input',
                    elementConfig:{
                        type: 'email',
                        placeholder: 'Ingresá tu email'
                    },
                    value: '',
                    validation:{
                        required: true
                    },
                    valid: false,
                    touched: false
                },
                telefono:{
                    elementType: 'input',
                    elementConfig:{
                        type: 'text',
                        placeholder: 'Telefono'
                    },
                    value: '',
                    validation:{
                        required: true
                    },
                    valid: false,
                    touched: false
                },
                
            },
            formaDePago: null,
            formIsValid: false,
            loading: false
        }

        componentDidMount(){
            
        }
        componentWillUnmount(){
            
        }
        
        orderHandler=(event)=>{
            this.setState({loading: true})
            event.preventDefault();
            const formData= {};
            for(let formElementIdentifier in this.state.orderForm){
                formData[formElementIdentifier] = this.state.orderForm[formElementIdentifier].value
            };
            
            let time=moment().format('MMMM Do YYYY, h:mm:ss a');
            const order={
                basket: this.props.basket,
                price: this.props.price,
                orderData: formData,
                formaDePago: this.props.formaDePago,
                pagado: false,
                entregado: false,
                date: time
            }

            

            axios.post('/orders.json', order)
            .then(response => {
                this.setState({loading: false})
               // this.props.history.push('/')
            })
            .catch(error => console.log(error))//this.props.onLoading());
        }
    
        checkValidity(value, rules){
            let isValid= true;

            if(rules.required){
                isValid = value.trim() !=='' && isValid;
            }

            if(rules.minLength){
                isValid = value.length >= rules.minLength && isValid;
            }

            return isValid;
        }

    inputChangeHandler=(event, inputIdentifier)=>{
            const updatedOrderForm = {
                ...this.state.orderForm
            }
            const updatedFormElement = {
                ...updatedOrderForm[inputIdentifier]
            }

            updatedFormElement.value = event.target.value;
            updatedFormElement.valid=this.checkValidity(updatedFormElement.value, updatedFormElement.validation);
            updatedFormElement.touched = true;
            updatedOrderForm[inputIdentifier]= updatedFormElement;

            let formIsValid = true;
            for(let inputIdentifier in updatedOrderForm){
                formIsValid=updatedOrderForm[inputIdentifier].valid && formIsValid;
            }
            this.setState({orderForm: updatedOrderForm, formIsValid: formIsValid});
        }

        pagoHandler = (event) =>{
            let formaDePago=event.target.value;
            this.props.onFormaDePagoSelected(formaDePago)
        }

    render() { 
        const arrayClasses=[classes.submitBtn, classes.disabled]
        const formElementsArray=[];
        for(let key in this.state.orderForm){
            formElementsArray.push({
                id: key,
                config: this.state.orderForm[key]
            });
        }

        let form=(
            <form className={classes.ContactData} onSubmit={this.orderHandler}>
                {formElementsArray.map(formElement =>{
                    return <Input 
                    key={formElement.id}
                    elementType={formElement.config.elementType}
                    elementConfig={formElement.config.elementConfig}
                    value={formElement.config.value}
                    invalid={!formElement.config.valid}
                    shouldValidate={formElement.config.validation}
                    touched={formElement.config.touched}
                    changed={(event)=>this.inputChangeHandler(event, formElement.id)}
                    />
                })}
                <div className={classes.containerPagos}>
                    <label>Transferencia Bancaria<input onChange={this.pagoHandler}required value="Transferencia Bancaria" name="payment" type="radio"/></label>
                    <label>MercadoPago<input onChange={this.pagoHandler} required value="MercadoPago" name="payment"  type="radio"/></label>
                </div>
               <button
               type="submit"
                className={this.props.formaDePago !== null && this.props.basket.length !== 0 && this.state.formIsValid ? arrayClasses[0] : arrayClasses.join(' ')}
                disabled={this.props.formaDePago === null && !this.state.formIsValid && this.props.basket.length === 0}
                >Proceder al pago</button>
            </form>
        )
            if(this.state.loading){
                form=<Spinner />
            }
        
        return ( 
            <Auxiliary>
               {form} 
            </Auxiliary>
            
         );
    }
}
 
const mapStateToProps = state =>{
    return{
        basket: state.carrito.basket,
        price: state.carrito.totalPrice,
        formaDePago: state.carrito.formaDePago
    }
}

const mapDispatchToProps = dispatch =>{
    return{
        onCreateOrder: (order)=>dispatch(actions.onCreateOrder(order)),
        onFormaDePagoSelected: (formaDePago) => dispatch(actions.onSelectFormaDePago(formaDePago))
    }
}


export default connect(mapStateToProps, mapDispatchToProps)(withRouter(withErrorHandler(ContactData, axios)));


