import React, { Component } from 'react';

import classes from './Home.module.css';
import Logo from './../../components/Ul/Logo/Logo';
import NavigationItems from './../../components/Navigation/NavigationItems';

class Home extends Component {
    state={
        showSubMenu: false
    }

    showSubMenuHandler=()=>{
        console.log('mouseenter')
        this.setState({showMenu: !this.state.showSubMenu})
    }
   
    render() { 

        let classesMenu=[classes.submenu]

        if(this.state.showMenu){
            classesMenu.push(classes.subMenuOpen)
        }

        return ( 
            <div className={classes.Home}>
                <NavigationItems showSubMenu={this.state.showSubMenu} showSubmenuHandler={this.showSubMenuHandler}/>
                <div className={classes.container}>
                    <Logo />
                    <h3>Respaldos a tu medida</h3>
                </div>
            </div>
         );
    }
}
 
export default Home;
