import React from 'react';
import Auxiliary from '../../../hoc/Auxiliary';
import ShoppingBasketOutlinedIcon from '@material-ui/icons/ShoppingBasketOutlined';

import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import classes from './CarritoLink.module.css';

function Carrito(props) {

    let infoCarrito;
    if(props.basket.length){
      return infoCarrito= props.basket.map(item => {
          return  Object.keys(item).map(itemKey =>{
              return [...Array(item[itemKey])].map(key =>{
               if(key === Object){
                   return Object.keys(key).map( prop => <span>{prop}</span> )
               }
               if(key === '') return;
              return <span><p>{itemKey}:{key}</p></span>
              })
          })
        })
    }
// let transformedIngredients = Object.keys(props.ingredients)
// .map((igKey) => {
//   return [...Array(props.ingredients[igKey])].map((_, i) => {
//     return <BurguerIngredient key={igKey + i} type={igKey} />;
//   });
// })
    return (
        <Auxiliary>
            <Link to="/carrito" className={classes.Carrito}><ShoppingBasketOutlinedIcon /></Link>
            <span className={classes}>
                {infoCarrito}
            </span>
        </Auxiliary>
    )
}

const mapStateToProps = state =>{
    return{
        basket: state.carrito.basket
    }
}


export default connect(mapStateToProps, null)(Carrito)
