    totalPrice: 0
}

const reducer = (state= initialState, action) => {
    console.log(state, action)
    switch (action.type) {
        case actionTypes.ADD_TO_BASKET:
            return{
                ...state
            }
        // case actionTypes.REMOVE_FROM_BASKET:
