import React from "react";

import classes from "./Formas.module.css";

const Formas = (props) => {
  
    const arrayClasses=[classes.item, classes.fill];


  const options = props.formas.map((forma, i) =>{ 
    let croquis='';
    if(forma.tipo === "Rectangular"){
      croquis=(
        <svg width="110" height="62" viewBox="0 0 110 62" fill="none" xmlns="http://www.w3.org/2000/svg">
        <rect x="0.5" y="0.5" width="108" height="61" fill="#FFFDFD" stroke="#1A1A1A"/>
        </svg>
      )
    }
      if(forma.tipo === "Capilla"){
croquis=(
  <svg width="110" height="62" viewBox="0 0 110 62" fill="none" xmlns="http://www.w3.org/2000/svg">
<path d="M0.5 61.75V13.1128C16.3815 4.88015 37.6709 0.749999 58 0.749999C78.3336 0.749998 97.6036 4.88149 109.5 13.0713V61.75H0.5Z" fill="white" stroke="black"/>
</svg>

)
      }
      if(forma.tipo === "Oval"){
        croquis=(
          <svg width="50" height="75" viewBox="0 0 50 75" fill="none" xmlns="http://www.w3.org/2000/svg">
<path d="M49.5 37.5C49.5 47.7704 46.7238 57.0459 42.2616 63.7392C37.7971 70.4359 31.6844 74.5 25 74.5C18.3156 74.5 12.2029 70.4359 7.73836 63.7392C3.27617 57.0459 0.5 47.7704 0.5 37.5C0.5 27.2296 3.27617 17.9541 7.73836 11.2608C12.2029 4.56407 18.3156 0.5 25 0.5C31.6844 0.5 37.7971 4.56407 42.2616 11.2608C46.7238 17.9541 49.5 27.2296 49.5 37.5Z" fill="white" stroke="black"/>
</svg> 
        )
      }
        if(forma.tipo === "Esquinas Redondas"){
          croquis=(
            <svg width="110" height="62" viewBox="0 0 110 62" fill="none" xmlns="http://www.w3.org/2000/svg">
            <rect x="0.5" y="0.5" width="109" height="61" rx="14.5" fill="white" stroke="black"/>
            </svg>
            
          )
              }
    
  
  <div key={forma.tipo} className={forma.select ? arrayClasses.join(' '):arrayClasses[0]} 
    onClick={() => props.selected(forma.tipo, forma.precio)}>
      <span>
      {croquis}
      </span>
      <span className={classes.tipo}>{forma.tipo}</span>
      <span className={classes.precio}>${forma.precio}</span>
    </div>}
  );
  return (
    <div className={classes.Formas}>
      <h2>Eleg√≠ la forma</h2>
      <div>{options}</div>
    </div>
  );
};

export default Formas;
