import React from "react";

import classes from "./GenerosYColores.module.css";

const GenerosYColores = (props) => {
  const arrayClasses = [classes.item, classes.fill];

  const form = props.generos.map((genero, i) => (
    <div
      className={props.generoSelected === genero.tipo? arrayClasses.join(" ") : arrayClasses[0]}
      key={genero.tipo}
      onClick={() => props.selectGenero(genero.tipo, genero.precio)}
    >
      <span className={classes.tipo}>{genero.tipo}</span>
      <span className={classes.precio}>${genero.precio}</span>
    </div>
  ));

  return (
    <div className={classes.GeneroYColor}>
      <h2>Elija un g√©nero</h2>
      <div>{form}</div>
    </div>
  );
};

export default GenerosYColores;
