import React from "react";

import Tiza from './FotosDeGeneros/Tiza.png';
import Aluminio from './FotosDeGeneros/Aluminio.png';
import Ceniza from './FotosDeGeneros/Ceniza.png';
import Carbón from './FotosDeGeneros/Carbon.png'

export const colores = (nombre) =>{
  let color={
    //Generos de Lino Paris
      'Tiza': Tiza,
      'Aluminio': Aluminio,
      // 'Stone': Stone,
      'Ceniza': Ceniza,
      // 'Grafito': Grafito,
      'Carbón': Carbón,
      // 'Negro': Negro,
      // 'Natural': Natural,
      // 'Niebla': Niebla,
      // 'Nougat': Nougat,
      // 'Madera': Madera,
      // 'Lila': Lila,
      // 'Pink': Pink,
      // 'Limón': Limón,
      // 'Agua': Agua,
      // 'Turquesa': Turquesa,
      // 'Blue': Blue,
      // 'Azul Lavado': AzulLavado,
      // 'Denim': Denim,
      // 'Marino': Marino,
      // 'Pimenton': Pimenton,
      // 'Lacre': Lacre,
      // 'Guinda': Guinda, 
      // 'Ginger': Ginger, 
      // 'Café': Café, 
      // 'Arena': Arena
  }
  return color[nombre];
}



export const ColorDeFondo = (props) => {
  const coloresHexadecimales = (nombre) =>{
    let color={
      //Generos de Lino Paris
        'Tiza': Tiza,
        'Aluminio': "#C1C2C4",
        // 'Stone': Stone,
        'Ceniza': Ceniza,
        // 'Grafito': Grafito,
        'Carbón': Carbón,
        // 'Negro': Negro,
        // 'Natural': Natural,
        // 'Niebla': Niebla,
        // 'Nougat': Nougat,
        // 'Madera': Madera,
        // 'Lila': Lila,
        // 'Pink': Pink,
        // 'Limón': Limón,
        // 'Agua': Agua,
        // 'Turquesa': Turquesa,
        // 'Blue': Blue,
        // 'Azul Lavado': AzulLavado,
        // 'Denim': Denim,
        // 'Marino': Marino,
        // 'Pimenton': Pimenton,
        // 'Lacre': Lacre,
        // 'Guinda': Guinda, 
        // 'Ginger': Ginger, 
        // 'Café': Café, 
        // 'Arena': Arena
    }
    return color[nombre];
  }
  
  let colorStyle={
    width: "100%",
    height:"100%",
    backgroundColor: `${coloresHexadecimales(props.respaldo.color)}`
  }

  
  return (
    <defs>
      <pattern
        id="imgpattern"
        x="0" y="0" width="100" height="100"
        patternUnits="userSpaceOnUse"
      >
        <div style={colorStyle}></div>
      </pattern>
    </defs>
  );
};
