import React from 'react';

import classes from './ItemOrder.module.css';
import DataRespaldo from "./../DataRespaldo/DataRespaldo";
import DataCliente from "./../DataCliente/DataCliente";

const  ItemOrder =(props) => {
    let clasesPago = [classes.nopagado, classes.pagado];
    let clasesEntregado = [classes.noentregado, classes.entregado];

    let pagado = (
        <label className={order.pagado ? clasesPago.join(" ") : clasesPago[0]}>
          {order.pagado ? 'PAGADO' :'NO PAGADO'}
          <input onChange={() => orderPagoHandler(order.id)} type="checkbox"  />
        </label>
      );

let entregado = (
  <label className={order.entregado ? clasesEntregado.join(" ") : clasesEntregado[0]}>
    {order.entregado ? "ENTREGADO" : "NO ENTREGADO"}
    <input onChange={() => formaDeEnvioHandler(order.id)} type="checkbox"  />
  </label>
);

return (
  <details className={classes.orden}>
    <summary>
      <span className={classes.nombre} >{order.orderData.nombre}</span>
      <span className={classes.date}>{order.date}</span>
      <span onClick={()=>deleteOrderHandler(order)}className={classes.btnEliminar}>ELIMINAR</span>
    </summary>
    
    <div className={classes.dataOrden}>
    <div className={classes.formaDePago}>Precio Final de la orden: ${order.price}</div>
      <div className={classes.pagosContainer}>{pagado}</div>
      <div className={classes.formaDePago}>Forma de pago: {order.formaDePago}</div> 
      {entregado}
      <div className={classes.formaDePago}>Forma de Envio: {order.formaDeEnvio}</div>
      <DataRespaldo basket={order.basket} />
      <DataCliente cliente={order.orderData} />

    </div>
    <button 
  onClick={()=>saveChanges(order)}
  className={classes.saveBtn}>Save Changes</button>
  </details>
);
}

export default ItemOrder
