import * as actionTypes from './../actions/actionTypes';

const initialState={
    respaldo:{
        forma: '',
        medida:{
            ancho: null,
            altura: null
        },
        genero:'',
        color: '',
        tacha:'',
        tipoTacha:'',
        modelo:''
    },
    precioRespaldo: 0
 }

// const newPrecio = (addPrice) => ({
//         ...state.precioRespaldo, 
//          respaldoPrecio: state.precioRespaldo + addPrice
//         });



 const reducer = (state=initialState, action) =>{
    console.log(state.precioRespaldo);
    switch (action.type) {
      case actionTypes.SET_FORMA:
 
            return{
                ...state,
                respaldo: {
                    ...state.respaldo,
                    forma: action.tipo
                },
                precioRespaldo: state.precioRespaldo + action.precio
            }
      case actionTypes.SET_MEDIDAS:

          
          
                return{
                    ...state,
                    respaldo: {
                        ...state.respaldo,
                        medida:{
                            ancho: action.ancho,
                            altura: action.altura
                        }
                    }
                }
     case actionTypes.SET_GENEROYCOLOR:
         let newGenerosyColores =[...state.opciones.generosycolores];
         const selectGenerosyColores = () =>{
             return newGenerosyColores.map(genero =>{
                 if(genero.tipo ===action.tipo){
                     
                     return {
                        ...genero,
                        select: true
                    }
                     
                     
                 }else{
                    
                    return{
                        ...genero,
                        select: false
                    }
                 }
             });
         }
    
                    return{
                        ...state,
                        opciones:{
                            ...state.opciones,
                            generosycolores:selectGenerosyColores()
                            
                        },
                        respaldo:{
                            ...state.respaldo,
                            genero: action.tipo
                        }
                    }
      case actionTypes.SET_COLOR:
          return{
              ...state,
              respaldo:{
                  ...state.respaldo,
                  color: action.color
              }
          }
      case actionTypes.SET_TACHA:
          let newTachas=[...state.opciones.tachas];
          
          const selectTachas = () =>{
             return newTachas.map(tacha =>{
                  if(tacha.tamanio === action.tamanio){
                      return{
                        ...tacha,
                        select: true
                    }

                  }else{
                    return{
                        ...tacha,
                        select: false
                    }
                  }
              })
          }
          
                        return{
                            ...state,
                            opciones:{
                                ...state.opciones,
                                tachas:selectTachas()
                                
                            },
                            respaldo: {
                                ...state.respaldo,
                                tipoTacha: action.tamanio
                            }
                        }
      case actionTypes.SET_TIPO_TACHA:
                            let newTipoTachas=[...state.opciones.tachas];
                            
                            const selectTipo = () =>{
                                return newTipoTachas.map(tamanio =>{
                                    if(tamanio.select){
                                        return{
                                            ...tamanio,
                                            tipo: tamanio.tipo.map(tipo=>{
                                                
                                                if(tipo.tipo === action.tipo){
                                                    return{
                                                        ...tipo,
                                                        select: true
                                                    }
                                                }else{
                                                    return{
                                                        ...tipo,
                                                        select: false
                                                    }
                                                }
                                            }
                                            
                                            )
                                        }
                                    }else{
                                        return{
                                            ...tamanio
                                        }
                                    }
                                })
                            }
                            return {
                                ...state,
                                opciones:{
                                    ...state.opciones,
                                    tachas: selectTipo()
                                    
                                },
                                respaldo: {
                                    ...state.respaldo,
                                    tipoTacha: action.tipo
                                }
                            }
     case actionTypes.SET_MODELO:
         console.log(state.opciones.modelo);
         let newModelos = [...state.opciones.modelos];
         const selectModelo = () => {
             return newModelos.map(modelo =>{
                 if(modelo.tipo === action.tipo){
                    return{
                        ...modelo,
                        select: true
                    }
                 }else{
                    return{
                        ...modelo,
                        select: false
                    }
                 }
             })
         }
                            return{
                                ...state,
                                opciones:{
                                    ...state.opciones,
                                    modelos:selectModelo()
                                    
                                },
                                respaldo: {
                                    ...state.respaldo,
                                    modelo: action.tipo
                                }
                            }
    
        default:
            return state;
    }
 }

 export default reducer;
