import React from "react";

import classes from "./Tachas.module.css";

const Tachas = (props) => {
  const opciones = props.tachas.map((tacha, i) => (
    <details className={classes.details}>
      <summary>{tacha.tamanio}</summary>
      <form>
          {tacha.tipo.map((tipo, i) =>(
              <label> 
          {i + 1}.{tipo.tipo}
          <input 
          checked={tipo.select ? true:false} 
          onChange={()=>props.selectTacha(tacha.tamanio, tacha.tipo)}
          type="checkbox" 
          value="OXIDO"/>
        </label>
          ))}
        <label>precio: ${tacha.precio.toFixed(2)}</label>
      </form>
    </details>
  ));
  return (
    <div className={classes.Tachas}>
      <h2>Tachas</h2>
      <div>
        {opciones}  
      </div> 
    </div>
  );
};

export default Tachas;
