        if (item.altura=== altura && item.ancho === ancho) {
          return {
            ...item,
            select: true,
          };
        } else {
          return {
            ...item,
            select: false,
          };
        }
      });

      const selectTacha = (opcion, tamanio) =>
      [...opcion].map(item => {
        if (item.tamanio === tamanio) {
          return {
            ...item,
            select: true,
          };
        } else {
          return {
            ...item,
            select: false,
          };
        }
      });

    //metodos para cambiar el respaldo...

    const setFormas = (tipo, precio) => {
      this.setState({
        opciones: {
          ...this.state.opciones,
          formas: selectItem(this.state.opciones.formas, tipo),
        }
      });
      this.props.onFormaSelected(tipo, precio);
    };

    const setMedida = (altura, ancho, precio) => {
      this.setState({
        opciones: {
          ...this.state.opciones,
          medidas: selectMedida(this.state.opciones.medidas, altura, ancho)
        },
      });
      this.props.onMedidaSelected(altura, ancho, precio);
    };

    const setGenero = (tipo, precio) => {
      this.setState({
        ...this.state,
        opciones: {
          ...this.state.opciones,
          generosycolores: selectItem(
            this.state.opciones.generosycolores,
            tipo
          ),
        },
      });
      this.props.onGeneroYColorSelected(tipo, precio);
    };

const setTipoDeTacha = (tipo) =>{
      this.setState(
      ()=>console.log(this.state))
      

      this.props.onTipoDeTachaSelected(tipo);
    }

    const setTacha = (tamanio, precio) => {
      this.setState({
        ...this.state,
        opciones: {
          ...this.state.opciones,
          tachas: selectTacha(this.state.opciones.tachas, tamanio),
        },
      });
      this.props.onTachaSelected(tamanio, precio);
    };

    

    const selectTipo = (opciones, tipo) => {

      return [...opciones].map((tamanio) => {
        
        if (tamanio.select) {
          return {
            ...tamanio,
            tipo: tamanio.tipo.map((item) => {
              
              if (item.tipo === tipo) {
                
                return {
                  ...item,
                  select: true,
                };
              } else {
               
                return {
                  ...item,
                  select: false,
                };
              }
            }),
          };
        } else {
          return {
            ...tamanio,
          };
        }
      });
    };

    

    const setModelo = (tipo, precio) =>{
      this.setState({
        ...this.state,
        opciones:{
          ...this.state.opciones,
        modelos: selectItem(this.state.opciones.modelos, tipo)
      }
    })
      this.props.onModeloSelected(tipo, precio)
    }

    return (
      <div className={classes.RespaldoBuilder}>
       <Header 
       index={this.state.index}
       toggleLeft={toggleRoutesHandlerLeft}
       toggleRight={toggleRoutesHandlerRight}/>
        <div className={classes.croquisRespaldo}>
          <ContainerRespaldo />
          <div className={classes.precioTotal}>
            Precio Total:
            <strong>${this.props.precioRespaldo.toFixed(2)}</strong>
          </div>
        </div>
        <div className={classes.control}>
          <Switch>
            <Route path={baseUrl + "/medidas"}>
              <Medidas
                medidas={this.state.opciones.medidas}
                selectMedida={setMedida}
              />
            </Route>
            <Route path={baseUrl + "/generosycolores"}>
              <GenerosYColores
                generos={this.state.opciones.generosycolores}
                selectGenero={setGenero}
              />
            </Route>
            <Route path={baseUrl + "/colores"}>
              <Colores
                color={this.state.opciones.color}
                generoParaColor={this.state.opciones.generosycolores}
                selectColor={this.props.onColorSelected}
              />
            </Route>
            <Route path={baseUrl + "/tachas"}>
              <Tachas
                tachas={this.state.opciones.tachas}
                selectTacha={setTacha}
                selectTipoDeTacha={setTipoDeTacha}
              />
            </Route>
            <Route path={baseUrl + "/modelos"}>
              <Modelos
                selectModelo={setModelo}
                modelos={this.state.opciones.modelos}
              />
            </Route>
            <Route path={baseUrl + "/"} exact>
              <Formas
                formas={this.state.opciones.formas}
                selected={setFormas}
              />
            </Route>
          </Switch>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    precioRespaldo: state.respaldo.precioParticular,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onFormaSelected: (tipo, precio) =>
      dispatch({ type: actions.SET_FORMA, tipo: tipo, precio: precio }),
    onMedidaSelected: (altura, ancho, precio) =>
      dispatch({ type: actions.SET_MEDIDAS, ancho: ancho, altura: altura, precio: precio }),
    onGeneroYColorSelected: (tipo, precio) =>
      dispatch({ type: actions.SET_GENEROYCOLOR, tipo: tipo, precio: precio }),
    onColorSelected: (color) =>
      dispatch({ type: actions.SET_COLOR, color: color}),
    onTachaSelected: (tamanio, precio) =>
      dispatch({ type: actions.SET_TACHA, tamanio: tamanio, precio: precio }),
    onTipoDeTachaSelected: (tipo) =>
      dispatch({ type: actions.SET_TIPO_TACHA, tipo: tipo}),
    onModeloSelected: (tipo, precio) =>
      dispatch({ type: actions.SET_MODELO, tipo: tipo, precio: precio }),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(RespaldoBuilder);
