import React, { Component } from 'react';
import Auxiliary from './../../hoc/Auxiliary';

import {Motion, spring, presets} from 'react-motion';
import classes from './Contacto.module.css'

class Contacto extends Component {
    state={
            animate: false
        }

    setAnimation(state){
        this.setState({animate: state});
    }
    render() { 
        
        return ( 
            <Auxiliary>
            <Motion
                style={
                    this.state.animate 
                    ?
                    {
                        scale: spring(1),
                    translateX: spring(0, presets.gentle)
                    }
                    : {translateX: -300, scale: 0}
                  }
                  >
            
                
            {interpolatesStyles => (

            <div
            style={{
                transform: `translateX(${interpolatesStyles.translateX}px)`,
                opacity: interpolatesStyles.opacity
            }}
            >
                <h1>Contacto</h1>
            </div>
            )}

            </Motion>
            <button onClick={() => this.setAnimation(true)}>Trigger Animation</button>
            <button onClick={() => this.setAnimation(false)}>Reset Animation</button>
            </Auxiliary>
         );
    }
}
 
export default Contacto;
