import React, { Component } from 'react';
import Auxiliary from './../../hoc/Auxiliary';
import { Switch, Route, withRouter } from 'react-router-dom';
import Pagina1 from './Pagina1';
import Pagina2 from './Pagina2';
import Pagina3 from './Pagina3';
import { TransitionGroup, CSSTransition } from 'react-transition-group';
import './slideTransitions.css'

import classes from './Contacto.module.css'

class Contacto extends Component{
    state={
        prevDepth: this.getPathDepth(this.props.location)
    }

    componentWillReceiveProps(){
        this.setState({prevDepth: this.getPathDepth(this.props.location)});
    }

    getPathDepth(location){
        let pathArr=location.pathname.split('/');
        pathArr = pathArr.filter(n => n !== "");
        return pathArr.length;
    }

    render(){
        const currentKey = location.pathname.split('/')[1];
        const location = this.props;
        const timeout = { enter: 800, exit: 400}

        return ( 
            <div className={classes.Contacto}>
                <h1 className={classes.title}>Contacto</h1>
                <div className={classes.container}>
                    <div>
                    
                    <div className={classes.info}>
                    <span>
                        Email:
                    </span>
                    <span>
                        marlacroze@yahoo.com
                    </span>
                    </div>
                    <div className={classes.info}>
                    <span>
                        Tel:
                    </span>
                    <span>
                        +54 911 5560 7284
                    </span>
                    </div>
                    </div>
                </div>

            <TransitionGroup 
            component="div"
            className="App">
                <CSSTransition 
                key={currentKey}
                timeout={timeout}
                className="pageSlider"
                mountOnEnter={false}
                unmountOnExit={true}>
                    <div 
                    className={this.getPathDepth(location) - this.state.prevDepth >= 0 ? "left" : "right"}>
                    <Switch 
                    location={location}>
                        <Route path="/pagina1" exact>
                            <Pagina1/>
                        </Route>
                        <Route path="/pagina2" exact>
                            <Pagina2/>
                        </Route>
                        <Route path="/pagina3" exact>
                            <Pagina3/>
                        </Route>
                    </Switch>
                    </div>
                </CSSTransition>
            </TransitionGroup>
            </div>
         );
    }
        
    
}
 
export default withRouter(Contacto);
