import React, { Component } from 'react';

import classes from './AdminLayout.module.css';
import { connect } from 'react-redux';
import axios from 'axios';
import Moment from 'react-moment';
import DataRespaldo from './../../components/Admin/DataRespaldo/DataRespaldo';
import DataCliente from './../../components/Admin/DataCliente/DataCliente';

class AdminLayout extends Component {

    state={
        orders:[],
        loading: false
    }

    componentDidMount(){
        this.setState({loading: true})
        axios.get('https://nookweb-5fb61.firebaseio.com/orders.json?auth=' + this.props.token)
        .then(response => {
            this.setState({loading: false})
            let data;
            let arrayOrders=[...this.state.orders]
            for(data in response.data){
                this.setState({orders: arrayOrders.push(response.data[data])});
            }
           

        })
        .catch(err => console.log(err))
    }

     
    render() { 
        
            let orders=this.state.orders.map(order=>{
            let pagado=<React.Fragment><span>PAGADO</span><span>cambiar a NO PAGADO</span></React.Fragment>
            if(!order.pagado){
                pagado=<React.Fragment><span>NO PAGADO</span><span>cambiar a PAGADO</span></React.Fragment>
            }

            return(
                <details className={classes.orden}>
                    <summary><span>{order.orderData.nombre}</span ><Moment className={classes.date}>{order.date}</Moment></summary>
                    <div className={classes.dataOrden}>
                    <div className={classes.pagado}>{pagado}</div>          
                    <DataRespaldo basket={order.basket}/>
                    <DataCliente cliente={order.orderData}/>
                    </div>
                </details>
            );
        })


        return ( 
            <div className={classes.AdminLayout}>
                <h2 className={classes.title}>Admin</h2>
                <button className={classes.logoutBtn}>Logout</button>
                <div className={classes.orderContainer}></div>
            </div>
         );
    }
}
 
const mapStateToProps = state =>{
    return{
        token: state.auth.token
    }
}

export default connect(mapStateToProps, null)(AdminLayout);
