import React, { Component } from 'react';
import Auxiliary from './../../hoc/Auxiliary';

import {Motion, spring, presets} from 'react-motion';

class Contacto extends Component {
    
    render() { 
        state={
            animate: false
        }
        return ( 
            <Auxiliary>
            <Motion
                defaultStyle={{
                    scale: 0,
                    translateX: -300
                  }}
                style={
                    startAnimation 
                    ?
                    {scale: spring(1),
                    translateX: spring(0, presets.gentle)}
                    : initialStyle
                  }
                  >
            
                
            {interpolatesStyles => (

            <div
            style={{
                transform: `translateX(${interpolatesStyles.translateX}px)`,
                opacity: interpolatesStyles.opacity
            }}
            >
                <h1>Contacto</h1>
            </div>
            )}

            </Motion>
            <button onClick={() => setAnimation(true)}>Trigger Animation</button>
            <button onClick={() => setAnimation(false)}>Reset Animation</button>
            </Auxiliary>
         );
    }
}
 
export default Contacto;
