import React from "react";

import classes from "./Tachas.module.css";

const Tachas = (props) => {

  const arrayClasses=[classes.summary, classes.fill];
  const arrayClasses1=[classes.tiposTachas]
  const arrayClasses2=[classes.tipo, classes.fill2];
  const triggerDetails = (event, tamanio, precio) =>{
    console.log(event)
    arrayClasses1.push(classes.open);

    props.selectTacha(tamanio, precio);
  }
  const opciones = props.tachas.map((tacha, i) => {
    
   return <div
    className={classes.details} 
    key={tacha.tamanio}
    onClick={() =>triggerDetails(tacha.tamanio, tacha.precio)}>
      <h2
      className={tacha.select ? arrayClasses.join(' ') : arrayClasses[0]} 
      key={tacha.tamanio}>{tacha.tamanio} <span>${tacha.precio.toFixed(2)}</span></h2>
      <div className={arrayClasses1.join(' ')}>
        {
          tacha.tipo.map( tipo =>{

            return <div
            className={props.tipoDeTacha===tipo.tipo ? arrayClasses2.join(' ') : arrayClasses2[0]} 
            key={Math.random()*100} 
            onClick={()=>props.selectTipoDeTacha(tipo.tipo)}>
              <img 
              alt="img_foto"
              src="https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcSD3X3H8ZEFWdCdWTqajSGyHUw8pDPiSxgcoA&usqp=CAU"/>
              <span>
              {tipo.tipo}
              </span>
              </div>
          })
        }

    </div>
    </div>
  });
  return (
    <div className={classes.Tachas}>
      <h2>Tachas</h2>
      <div>
        {opciones}  
      </div> 
    </div>
  );
};

export default Tachas;
