import React from 'react';

import * as actions from './../../../store/actions/actionTypes';
import classes from './ResumenRespaldo.module.css';
import { connect } from 'react-redux';
import Modal from './../../Ul/Modal/Modal';
import Message from './../../Message/message';

function ResumenRespaldo(props) {

    const arrayClasses =[ classes.Button, classes.Disabled];
    
    const checkFeatures= Object.values(props.caracteristicas).map( item => {
        if(item === 'no seleccionado'){
            return item
        }});

    return (
        <div className={classes.ResumenRespaldo}>
            <h2>Resumen Respaldo</h2>
            <ul className={classes.listaPrincipal}>
                <li>Envio: tu modelo tarda 10 dias habiles</li>
                <li>Caracteristicas:
                    <ul className={classes.Caracteristicas}>
                        <li>Forma: {props.caracteristicas.forma}</li>
                        <li>Medidas: 
                            <ul className={classes.Medidas}>
                                <li>altura: {props.caracteristicas?.medida.altura ?props.caracteristicas?.medida.altura.toFixed(2): '0'} m.</li>
                                <li>ancho: {props.caracteristicas?.medida.ancho ?props.caracteristicas?.medida.ancho.toFixed(2): '0'} m.</li>
                            </ul>
                            </li>
                         <li>Genero: {props.caracteristicas.genero}</li>  
                         <li>Color: {props.caracteristicas.color}</li>  
                        <li>Tachas: {props.caracteristicas.tachas} {props.caracteristicas.tipoTacha}</li> 
                        <li>Modelo: {props.caracteristicas.modelo}</li> 
                    </ul>
                </li>
                <li>Precio final:</li>
            </ul>
            <button 
           
            onClick={console.log(checkFeatures)}
            className={classes.Button}>AGREGAR AL CARRITO</button>
            <Modal >
               {/* checkFeatures ? <p>Hay al menos una caracteristica sin seleccionar. Por favor, chequee</p> <Message/> */}
            </Modal>
        </div>
    )
}

const mapStateToProps = state =>{
    return{
            respaldo: state.respaldo,
            caracteristicas: state.respaldo.respaldo,
    }
}


const mapDispatchToProps = dispatch =>{
    return{
        onProductAddedToBasket: (producto) => dispatch({type: actions.ADD_TO_BASKET, producto: producto})
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(ResumenRespaldo);
