import React, { Component } from 'react';

import {Route, Switch } from 'react-router-dom';
import classes from './RespaldoBuilder.module.css';
import Auxiliary from './../../hoc/Auxiliary';
import Respaldo from './../../components/Respaldo/Respaldo/Respaldo';
import Formas from './../../components/Respaldo/OpcionesRespaldo/Formas/Formas';
import Medidas from './../../components/Respaldo/OpcionesRespaldo/Medidas/Medidas';
import GenerosYColores from './../../components/Respaldo/OpcionesRespaldo/GenerosYColores/GenerosYColores';
import Tachas from './../../components/Respaldo/OpcionesRespaldo/Tachas/Tachas';
import Modelos from './../../components/Respaldo/OpcionesRespaldo/Modelos/Modelos';
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';

class RespaldoBuilder extends Component {
    
    render() { 
        console.log(this.props)
        const toggleRoutesHandler =(location) =>{
        const keys = ["/", "/formas", "/medidas", "/generosycolores", "/tachas", "/modelos"];
        const baseUrl = "/respaldo-options/respaldo-builder";
        let url;

        if(location == baseUrl){
            url=keys[0];
        }
        if(location == baseUrl + keys[0]){
            url=keys[1]
        }
        
            this.props.push( location + url)
        }
       
        return ( 
            <div className={classes.RespaldoBuilder}>
                <Respaldo />
                
                <div className={classes.control} onClick={()=>toggleRoutesHandler(this.props.location.pathname)}>
                <ArrowForwardIosIcon className={classes.ArrowLeft}/>
                <Switch>
                    <Route path="/medida" component={Medidas}/>
                    <Route path="/generosycolores" component={GenerosYColores}/>
                    <Route path="/tachas" component={Tachas}/>
                    <Route path="/modelos" component={Modelos}/>
                    <Route path="/" component={Formas}/>
                </Switch>
                <ArrowBackIosIcon className={classes.ArrowRight} onClick={()=>toggleRoutesHandler(this.props.location.pathname)}/>
                    </div>
            </div>
         );
    }
}
 
export default RespaldoBuilder;
