import React from "react";

import classes from "./Colores.module.css";

function Colores(props) {
  
  

  let options = null;
  if (props.generoParaColor === "no seleccionado") {
    return (options = (
      <p className={classes.mensaje}>
        Para seleccionar un color, primero debe seleccionar un g√©nero. Por favor, vuelva al paso
        anterior.
      </p>
    ));
  } else {
    let index=props.color.findIndex(item => item.tipo === props.generoParaColor);
    options = props.color[index].map((color) => (
      <span key={color} onClick={() => props.selectColor(color)}>
        <div className={classes.colorImg}></div>
        <div className={classes.nombreColor}>{color}</div>
      </span>
    ));
  }

  return (
    <div className={classes.Colores}>
      <h2>Elegi tu color</h2>
      <div>{options}</div>
    </div>
  );
}

export default Colores;
