    const toggleRoutesHandlerRight = (location) => {
      location = location.replace(baseUrl, "");

      let index = keys.indexOf(location);

      if (keys[index + 1] === undefined) {
        return this.props.history.push("/resumen-respaldo");
      }
      if (index === -1) {
        index = 0;
      }
      this.setState({ index: index + 1 });
      return this.props.history.push(baseUrl + keys[index + 1]);
    };

    const routes = [
      "Formas",
      "Medidas",
      "Generos",
      "Colores",
      "Tachas",
      "Modelos",
    ];
    const pathname = this.props.history.location.pathname;

    const selectItem = (opcion, arg1) =>
      [...opcion].map((item) => {
        console.log(item[arg1]);
        if (Object.values(item)=== arg1) {
          return {
            ...item,
            select: true,
          };
        } else {
          return {
            ...item,
            select: false,
          };
        }
      });

    //metodos para cambiar el respaldo...

    const setFormas = (tipo, precio) => {
      this.setState({
        opciones: {
          ...this.state.opciones,
          formas: selectItem(this.state.opciones.formas, tipo),
        }
      });
      this.props.onFormaSelected(tipo, precio);
    };

    const setMedida = (altura, ancho, precio) => {
      this.setState({
        opciones: {
          medidas: selectItem(this.state.opciones.medidas, ancho)
        },
      });
      this.props.onMedidaSelected(altura, ancho, precio);
    };

    const setGenero = (tipo, precio) => {
      this.setState({
        ...this.state,
        opciones: {
          ...this.state.opciones,
          generosycolores: selectItem(
            this.state.opciones.generosycolores,
            tipo
          ),
        },
      });
      this.props.onGeneroYColorSelected(tipo, precio);
    };

    const setTacha = (tamanio, precio) => {
      this.setState({
        ...this.state,
        opciones: {
          ...this.state.opciones,
          tachas: selectItem(this.state.opciones.tachas, tamanio),
        },
      });
      this.props.onTachaSelected(tamanio, precio);
    };

    

    const selectTipo = () => {
      let newTipoTachas = [...this.state.opciones.tachas];
      return newTipoTachas.map((tamanio) => {
        if (tamanio.select) {
          return {
            ...tamanio,
            tipo: tamanio.tipo.map((tipo) => {
              if (tipo.tipo === tipo) {
                return {
                  ...tipo,
                  select: true,
                };
              } else {
                return {
                  ...tipo,
                  select: false,
                };
              }
            }),
          };
        } else {
          return {
            ...tamanio,
          };
        }
      });
    };

    const setTipoDeTacha = (tipo) =>{
      this.setState({
        ...this.state,
        tachas: selectTipo(tipo)});
      this.props.onTipoDeTachaSelected(tipo)
    }
    console.log(this.state);
    const setModelo = (tipo, precio) =>{
      this.setState({
        modelos: selectItem(this.state.opciones.modelos, tipo)
      });
      this.props.onModeloSelected(tipo, precio)
    }

    return (
      <div className={classes.RespaldoBuilder}>
        <div className={classes.headerRespaldo}>
          <ArrowForwardIosIcon
            className={classes.ArrowRight}
            onClick={() => toggleRoutesHandlerRight(pathname)}
          />

          <h2>
            #{this.state.index + 1}: {routes[this.state.index]}
          </h2>

          <ArrowBackIosIcon
            className={classes.ArrowLeft}
            onClick={() => toggleRoutesHandlerLeft(pathname)}
          />
          <Mapa routes={routes} index={this.state.index} />
        </div>
        <div className={classes.croquisRespaldo}>
          <ContainerRespaldo />
          <div className={classes.precioTotal}>
            Precio Total:
            <strong>${this.props.precioRespaldo.toFixed(2)}</strong>
          </div>
        </div>
        <div className={classes.control}>
          <Switch>
            <Route path={baseUrl + "/medidas"}>
              <Medidas
                medidas={this.state.opciones.medidas}
                selectMedida={setMedida}
              />
            </Route>
            <Route path={baseUrl + "/generosycolores"}>
              <GenerosYColores
                generos={this.state.opciones.generosycolores}
                selectGenero={setGenero}
              />
            </Route>
            <Route path={baseUrl + "/colores"}>
              <Colores
                color={this.state.opciones.color}
                generoParaColor={this.state.opciones.generosycolores}
                selectColor={this.props.onColorSelected}
              />
            </Route>
            <Route path={baseUrl + "/tachas"}>
              <Tachas
                tachas={this.state.opciones.tachas}
                selectTacha={setTacha}
                selectTipoDeTacha={setTipoDeTacha}
              />
            </Route>
            <Route path={baseUrl + "/modelos"}>
              <Modelos
                selectModelo={setModelo}
                modelos={this.state.opciones.modelos}
              />
            </Route>
            <Route path={baseUrl + "/"} exact>
              <Formas
                formas={this.state.opciones.formas}
                selected={setFormas}
              />
            </Route>
          </Switch>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    precioRespaldo: state.respaldo.precioRespaldo,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onFormaSelected: (tipo, precio) =>
      dispatch({ type: actions.SET_FORMA, tipo: tipo, precio: precio }),
    onMedidaSelected: (altura, ancho) =>
      dispatch({ type: actions.SET_MEDIDAS, ancho: ancho, altura: altura }),
    onGeneroYColorSelected: (tipo) =>
      dispatch({ type: actions.SET_GENEROYCOLOR, tipo: tipo }),
    onColorSelected: (color) =>
      dispatch({ type: actions.SET_COLOR, color: color }),
    onTachaSelected: (tamanio) =>
      dispatch({ type: actions.SET_TACHA, tamanio: tamanio }),
    onTipoDeTachaSelected: (tipo) =>
      dispatch({ type: actions.SET_TIPO_TACHA, tipo: tipo }),
    onModeloSelected: (tipo) =>
      dispatch({ type: actions.SET_MODELO, tipo: tipo }),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(RespaldoBuilder);
